import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import RubiksCube from './components/RubiksCube';
import Controls from './components/Controls';
import InfoPanel from './components/InfoPanel';
import ShapeButtons from './components/ShapeButtons';
import FaceButtons from './components/FaceButtons';
import IdentifyIncorrectShapes from './components/IdentifyIncorrectShapes';

const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 20px;
`;

const Header = styled.header`
  text-align: center;
  margin-bottom: 30px;
  color: white;
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 10px;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  opacity: 0.9;
`;

const MainContent = styled.main`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 30px;
  width: 100%;
  max-width: 1200px;
`;

const CubeContainer = styled.div`
  width: 100%;
  height: 600px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  overflow: hidden;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
`;

function App() {
  const [isRotating, setIsRotating] = useState(false);
  const [autoRotate, setAutoRotate] = useState(false);
  const [isScrambling, setIsScrambling] = useState(false);
  const [currentScramble, setCurrentScramble] = useState('');
  const [cubeState, setCubeState] = useState(null);
  const scrambleRef = useRef();
  const resetRef = useRef();

  const handleScramble = () => {
    if (scrambleRef.current) {
      setIsScrambling(true);
      setCurrentScramble('Generating scramble sequence...');
      scrambleRef.current();

      // Update scramble display after a delay
      setTimeout(() => {
        setCurrentScramble('F B R L U D F\' B\' R\' L\' U\' D\' F2 B2 R2 L2 U2 D2');
        setTimeout(() => {
          setIsScrambling(false);
        }, 7000);
      }, 1000);
    }
  };

  const handleReset = () => {
    if (resetRef.current) {
      setCurrentScramble('');
      resetRef.current();
    }
  };

  const handleShapeClick = (pieceId, pieceInfo) => {
    // Log to main development terminal with enhanced visibility
    console.log('\n' + 'üöÄ'.repeat(40));
    console.log(`üéØ SHAPE BUTTON CLICKED IN MAIN TERMINAL!`);
    console.log(`üÜî Piece ID: ${pieceId}`);
    console.log(`üé® Shape: ${pieceInfo.shapeColor} ${pieceInfo.shapeType}`);
    console.log(`üìç Current Position: [${pieceInfo.currentPosition.join(', ')}]`);
    console.log(`üéØ Expected Position: [${pieceInfo.expectedPosition.join(', ')}]`);
    console.log(`‚úÖ Is Correct: ${pieceInfo.isInCorrectPosition}`);
    console.log(`üìä Move History: ${pieceInfo.rotationHistory.length} moves`);
    console.log('üöÄ'.repeat(40) + '\n');

    // Force output to the React development server terminal
    console.error(`\nüéØ SHAPE CLICK: ${pieceInfo.shapeColor} ${pieceInfo.shapeType} (Piece ${pieceId})`);
    console.error(`üìç Position: [${pieceInfo.currentPosition.join(', ')}] -> [${pieceInfo.expectedPosition.join(', ')}]`);
    console.error(`‚úÖ Correct: ${pieceInfo.isInCorrectPosition}\n`);

    // Try to write directly to stdout (this should appear in the React dev server terminal)
    if (typeof process !== 'undefined' && process.stdout) {
      process.stdout.write(`\nüéØ SHAPE BUTTON CLICKED: ${pieceInfo.shapeColor} ${pieceInfo.shapeType} (Piece ${pieceId})\n`);
      process.stdout.write(`üìç Current: [${pieceInfo.currentPosition.join(', ')}] Expected: [${pieceInfo.expectedPosition.join(', ')}]\n`);
      process.stdout.write(`‚úÖ Is Correct: ${pieceInfo.isInCorrectPosition}\n\n`);
    }

    // Additional handling can be added here if needed
  };

  const handleFaceClick = (pieceId, faceName, faceInfo) => {
    // Log to main development terminal with enhanced visibility
    console.log('\n' + 'üé®'.repeat(40));
    console.log(`üé≠ FACE BUTTON CLICKED IN MAIN TERMINAL!`);
    console.log(`üÜî Piece ID: ${pieceId}`);
    console.log(`üé® Shape: ${faceInfo.shapeColor} ${faceInfo.shapeType}`);
    console.log(`üé≠ Face: ${faceName.toUpperCase()}`);
    console.log(`üé® Face Color: ${faceInfo.faceColor}`);
    console.log(`üìç Current Position: [${faceInfo.currentPosition.join(', ')}]`);
    console.log(`üéØ Expected Position: [${faceInfo.expectedPosition.join(', ')}]`);
    console.log(`‚úÖ Is Correct: ${faceInfo.isInCorrectPosition}`);
    console.log(`üìä Move History: ${faceInfo.rotationHistory.length} moves`);
    console.log('üé®'.repeat(40) + '\n');

    // Force output to the React development server terminal
    console.error(`\nüé≠ FACE CLICK: ${faceInfo.shapeColor} ${faceInfo.shapeType} (Piece ${pieceId}) - ${faceName.toUpperCase()} face`);
    console.error(`üé® Face Color: ${faceInfo.faceColor}`);
    console.error(`üìç Position: [${faceInfo.currentPosition.join(', ')}] -> [${faceInfo.expectedPosition.join(', ')}]`);
    console.error(`‚úÖ Correct: ${faceInfo.isInCorrectPosition}\n`);

    // Try to write directly to stdout
    if (typeof process !== 'undefined' && process.stdout) {
      process.stdout.write(`\nüé≠ FACE BUTTON CLICKED: ${faceInfo.shapeColor} ${faceInfo.shapeType} (Piece ${pieceId}) - ${faceName.toUpperCase()} face\n`);
      process.stdout.write(`üé® Face Color: ${faceInfo.faceColor}\n`);
      process.stdout.write(`üìç Current: [${faceInfo.currentPosition.join(', ')}] Expected: [${faceInfo.expectedPosition.join(', ')}]\n`);
      process.stdout.write(`‚úÖ Is Correct: ${faceInfo.isInCorrectPosition}\n\n`);
    }

    // Additional handling can be added here if needed
  };

  const handleIdentification = (identificationResult) => {
    // Log identification results to main development terminal
    console.log('\n' + 'üîç'.repeat(50));
    console.log(`üéØ IDENTIFICATION RESULT IN MAIN TERMINAL!`);
    console.log(`üìä Type: ${identificationResult.type}`);
    console.log(`üî¢ Count: ${identificationResult.count} incorrect pieces found`);

    if (identificationResult.type === 'shape') {
      console.log(`üî∑ Shape Type: ${identificationResult.shapeType}`);
    } else if (identificationResult.type === 'color') {
      console.log(`üé® Shape Color: ${identificationResult.shapeColor}`);
    } else if (identificationResult.type === 'border') {
      console.log(`üé≠ Border Color: ${identificationResult.borderColorName} (${identificationResult.borderColor})`);
    }

    console.log(`üìã Incorrect Pieces:`, identificationResult.incorrectPieces);
    console.log('üîç'.repeat(50) + '\n');

    // Force output to the React development server terminal
    console.error(`\nüîç IDENTIFICATION: ${identificationResult.type.toUpperCase()} - ${identificationResult.count} incorrect pieces`);
    console.error(`üìã Details:`, identificationResult.incorrectPieces.map(p => 
      `${p.shapeColor} ${p.shapeType} (Piece ${p.pieceId}) at [${p.currentPosition.join(', ')}]`
    ).join(', '));

    // Try to write directly to stdout
    if (typeof process !== 'undefined' && process.stdout) {
      process.stdout.write(`\nüîç IDENTIFICATION RESULT: ${identificationResult.type.toUpperCase()}\n`);
      process.stdout.write(`Count: ${identificationResult.count} incorrect pieces\n`);
      process.stdout.write(`Details: ${identificationResult.incorrectPieces.length} pieces identified\n\n`);
    }
  };

  return (
    <AppContainer>
      <Header>
        <Title>Rubik's Cube</Title>
        <Subtitle>Interactive 3D Cube with Three.js & React</Subtitle>
      </Header>

      <MainContent>
        <CubeContainer>
          <RubiksCube 
            isRotating={isRotating}
            autoRotate={autoRotate}
            onScramble={(scrambleFn) => { scrambleRef.current = scrambleFn; }}
            onReset={(resetFn) => { resetRef.current = resetFn; }}
            onCubeStateChange={setCubeState}
          />
        </CubeContainer>

        <Controls 
          isRotating={isRotating}
          setIsRotating={setIsRotating}
          autoRotate={autoRotate}
          setAutoRotate={setAutoRotate}
          onScramble={handleScramble}
          onReset={handleReset}
          cubeState={cubeState}
        />

        <InfoPanel 
          currentScramble={currentScramble}
          isScrambling={isScrambling}
        />

        <ShapeButtons
          isScrambling={isScrambling}
          cubeState={cubeState}
          onShapeClick={handleShapeClick}
        />

        <FaceButtons
          isScrambling={isScrambling}
          cubeState={cubeState}
          onFaceClick={handleFaceClick}
        />
      </MainContent>
    </AppContainer>
  );
}

export default App;
