import React, { useRef, useMemo, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import * as THREE from 'three';

// Individual cube piece component with improved materials
function CubePiece({ position, colors, size = 0.95 }) {
  const meshRef = useRef();
  
  const colorMap = {
    'white': '#ffffff',
    'yellow': '#ffff00',
    'red': '#ff0000',
    'orange': '#ff6600',
    'blue': '#0066ff',
    'green': '#00ff00',
    'black': '#1a1a1a'
  };

  // Create a solid cube geometry
  const geometry = useMemo(() => new THREE.BoxGeometry(size, size, size), [size]);
  
  // Get the main color for this cube piece (first color in the array)
  const mainColor = colors[0] || 'white';

  return (
    <mesh ref={meshRef} position={position} geometry={geometry}>
      <meshPhongMaterial 
        color={colorMap[mainColor]}
        side={THREE.DoubleSide}
        shininess={30}
        specular={0x444444}
      />
    </mesh>
  );
}

// Main cube group component with enhanced structure
function CubeGroup({ isRotating, autoRotate = false }) {
  const groupRef = useRef();
  const [rotationSpeed] = useState({ x: 0.005, y: 0.01 });
  
  useFrame((state) => {
    if (autoRotate && groupRef.current) {
      groupRef.current.rotation.y += rotationSpeed.y;
      groupRef.current.rotation.x += rotationSpeed.x;
    }
  });

  // Generate complete 3x3x3 cube structure
  const cubePieces = useMemo(() => {
    const pieces = [];
    
    // Create all 27 pieces (3x3x3)
    for (let x = -1; x <= 1; x++) {
      for (let y = -1; y <= 1; y++) {
        for (let z = -1; z <= 1; z++) {
                     const position = [x * 1.0, y * 1.0, z * 1.0];
          
                     // Determine the main color for each cube piece based on its position
           let mainColor = 'white'; // default color
           
           // Simple color assignment based on position
           if (z === 1) {
             // Front face pieces
             mainColor = 'white';
           } else if (z === -1) {
             // Back face pieces
             mainColor = 'yellow';
           } else if (x === 1) {
             // Right face pieces
             mainColor = 'red';
           } else if (x === -1) {
             // Left face pieces
             mainColor = 'orange';
           } else if (y === 1) {
             // Top face pieces
             mainColor = 'blue';
           } else if (y === -1) {
             // Bottom face pieces
             mainColor = 'green';
           } else {
             // Center piece
             mainColor = 'white';
           }
          
                     pieces.push({
             position,
             colors: [mainColor, mainColor, mainColor, mainColor, mainColor, mainColor]
           });
        }
      }
    }
    
    return pieces;
  }, []);

  return (
    <group ref={groupRef}>
      {cubePieces.map((piece, index) => (
        <CubePiece
          key={index}
          position={piece.position}
          colors={piece.colors}
        />
      ))}
    </group>
  );
}

// Main Rubik's Cube component
function RubiksCube({ isRotating, autoRotate = false }) {
  return (
    <Canvas
      camera={{ position: [6, 6, 6], fov: 60 }}
      style={{ width: '100%', height: '100%' }}
      gl={{ antialias: true, alpha: true }}
    >
      {/* Enhanced Lighting */}
      <ambientLight intensity={0.6} />
      <directionalLight 
        position={[10, 10, 5]} 
        intensity={1.5}
        castShadow
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
      />
      <pointLight position={[-10, -10, -5]} intensity={1.0} />
      <spotLight
        position={[0, 10, 0]}
        angle={0.3}
        penumbra={1}
        intensity={0.8}
        castShadow
      />
      
      {/* Environment */}
      <Environment preset="city" />
      
      {/* Cube */}
      <CubeGroup isRotating={isRotating} autoRotate={autoRotate} />
      
      {/* Controls */}
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        autoRotate={false}
        autoRotateSpeed={1}
        minDistance={3}
        maxDistance={15}
        dampingFactor={0.05}
        enableDamping={true}
      />
    </Canvas>
  );
}

export default RubiksCube;
