import React, { useRef, useMemo, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import * as THREE from 'three';

// Individual cube piece component with improved materials
function CubePiece({ position, colors, size = 0.95 }) {
  const meshRef = useRef();
  
  const colorMap = {
    'white': '#ffffff',
    'yellow': '#ffd500',
    'red': '#ff0000',
    'orange': '#ff8c00',
    'blue': '#0051ba',
    'green': '#00aa00',
    'black': '#1a1a1a'
  };

  // Create a solid cube geometry
  const geometry = useMemo(() => new THREE.BoxGeometry(size, size, size), [size]);
  
  // Determine the main color for this cube piece (most prominent color)
  const mainColor = colors.find(color => color !== 'black') || 'white';

  return (
    <mesh ref={meshRef} position={position} geometry={geometry}>
      <meshPhongMaterial 
        color={colorMap[mainColor]}
        side={THREE.DoubleSide}
      />
    </mesh>
  );
}

// Main cube group component with enhanced structure
function CubeGroup({ isRotating, autoRotate = false }) {
  const groupRef = useRef();
  const [rotationSpeed] = useState({ x: 0.005, y: 0.01 });
  
  useFrame((state) => {
    if (autoRotate && groupRef.current) {
      groupRef.current.rotation.y += rotationSpeed.y;
      groupRef.current.rotation.x += rotationSpeed.x;
    }
  });

  // Generate complete 3x3x3 cube structure
  const cubePieces = useMemo(() => {
    const pieces = [];
    
    // Create all 27 pieces (3x3x3)
    for (let x = -1; x <= 1; x++) {
      for (let y = -1; y <= 1; y++) {
        for (let z = -1; z <= 1; z++) {
                     const position = [x * 1.0, y * 1.0, z * 1.0];
          
                     // Determine colors for each face based on position
           const pieceColors = ['white', 'yellow', 'red', 'orange', 'blue', 'green'];
           
           // Apply colors to ALL faces of each piece based on their position
           // This ensures every face of every cube piece has its proper color
           
           // Front face (z = 1) - white
           if (z === 1) pieceColors[0] = 'white';
           // Back face (z = -1) - yellow  
           if (z === -1) pieceColors[1] = 'yellow';
           // Right face (x = 1) - red
           if (x === 1) pieceColors[2] = 'red';
           // Left face (x = -1) - orange
           if (x === -1) pieceColors[3] = 'orange';
           // Top face (y = 1) - blue
           if (y === 1) pieceColors[4] = 'blue';
           // Bottom face (y = -1) - green
           if (y === -1) pieceColors[5] = 'green';
           
           // For inner pieces, assign colors based on their position in the grid
           // This ensures all faces have proper colors, not just black
           if (x === 0 && y === 0 && z === 0) {
             // Center piece - assign colors based on adjacent faces
             pieceColors[0] = 'white';  // front
             pieceColors[1] = 'yellow'; // back
             pieceColors[2] = 'red';    // right
             pieceColors[3] = 'orange'; // left
             pieceColors[4] = 'blue';   // top
             pieceColors[5] = 'green';  // bottom
           } else if (x === 0 && y === 0) {
             // Edge pieces (center of edges)
             if (z === 1) pieceColors[0] = 'white';
             if (z === -1) pieceColors[1] = 'yellow';
             pieceColors[2] = 'red';
             pieceColors[3] = 'orange';
             pieceColors[4] = 'blue';
             pieceColors[5] = 'green';
           } else if (x === 0 && z === 0) {
             // Edge pieces (center of edges)
             pieceColors[0] = 'white';
             pieceColors[1] = 'yellow';
             pieceColors[2] = 'red';
             pieceColors[3] = 'orange';
             if (y === 1) pieceColors[4] = 'blue';
             if (y === -1) pieceColors[5] = 'green';
           } else if (y === 0 && z === 0) {
             // Edge pieces (center of edges)
             pieceColors[0] = 'white';
             pieceColors[1] = 'yellow';
             if (x === 1) pieceColors[2] = 'red';
             if (x === -1) pieceColors[3] = 'orange';
             pieceColors[4] = 'blue';
             pieceColors[5] = 'green';
           }
          
          pieces.push({
            position,
            colors: pieceColors
          });
        }
      }
    }
    
    return pieces;
  }, []);

  return (
    <group ref={groupRef}>
      {cubePieces.map((piece, index) => (
        <CubePiece
          key={index}
          position={piece.position}
          colors={piece.colors}
        />
      ))}
    </group>
  );
}

// Main Rubik's Cube component
function RubiksCube({ isRotating, autoRotate = false }) {
  return (
    <Canvas
      camera={{ position: [6, 6, 6], fov: 60 }}
      style={{ width: '100%', height: '100%' }}
      gl={{ antialias: true, alpha: true }}
    >
      {/* Enhanced Lighting */}
      <ambientLight intensity={0.3} />
      <directionalLight 
        position={[10, 10, 5]} 
        intensity={1.2}
        castShadow
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
      />
      <pointLight position={[-10, -10, -5]} intensity={0.8} />
      <spotLight
        position={[0, 10, 0]}
        angle={0.3}
        penumbra={1}
        intensity={0.5}
        castShadow
      />
      
      {/* Environment */}
      <Environment preset="city" />
      
      {/* Cube */}
      <CubeGroup isRotating={isRotating} autoRotate={autoRotate} />
      
      {/* Controls */}
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        autoRotate={false}
        autoRotateSpeed={1}
        minDistance={3}
        maxDistance={15}
        dampingFactor={0.05}
        enableDamping={true}
      />
    </Canvas>
  );
}

export default RubiksCube;
