import React, { useState, useRef, useMemo, useCallback, useEffect } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import * as THREE from 'three';

// Custom logging function that will be visible in terminal
const logToTerminal = (message, data = null) => {
  // Log to browser console
  console.log(`\nðŸŽ¯ ${message}`);
  if (data) {
    console.log(JSON.stringify(data, null, 2));
  }
  console.log('='.repeat(80) + '\n');
  
  // Send to log server to print in terminal
  fetch('http://localhost:3001/log', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      message: message,
      data: data
    })
  })
  .then(response => {
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    console.log('âœ… Log sent to terminal successfully');
  })
  .catch(error => {
    console.error('âŒ Could not send log to terminal server:', error);
    console.log('ðŸ’¡ Make sure the log server is running with: npm run log-server');
  });
};

// Function to create unique shapes for each piece
function createShape(pieceId, size) {
  const shapes = [
    // 0-4: Basic geometric shapes (Squares)
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="red" /></mesh>, // Square
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="blue" /></mesh>, // Square
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="green" /></mesh>, // Square
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="orange" /></mesh>, // Square
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="purple" /></mesh>, // Square

    // 5-9: Circles
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="red" /></mesh>, // Circle
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="blue" /></mesh>, // Circle
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="green" /></mesh>, // Circle
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="orange" /></mesh>, // Circle
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="yellow" /></mesh>, // Circle

    // 10-14: Triangles (using planeGeometry for now, ideally custom geometry)
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="red" /></mesh>, // Triangle
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="blue" /></mesh>, // Triangle
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="green" /></mesh>, // Triangle
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="orange" /></mesh>, // Triangle
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="purple" /></mesh>, // Triangle

    // 15-19: Diamonds (rotated squares)
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="red" /></mesh></group>, // Diamond
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="blue" /></mesh></group>, // Diamond
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="green" /></mesh></group>, // Diamond
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="orange" /></mesh></group>, // Diamond
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="purple" /></mesh></group>, // Diamond

    // 20-25: Complex patterns (Squares with white centers)
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="red" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="blue" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="green" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="orange" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="purple" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="yellow" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group> // Square with white center
  ];
  
  const shapeIndex = pieceId; // Changed from pieceId % shapes.length
  return shapes[shapeIndex]();
}

// Individual cube piece component
function CubePiece({ position, colors, size = 0.95, pieceId = 0 }) {
  const meshRef = useRef();
  
  // Log piece information for debugging
  React.useEffect(() => {
    const shapeTypes = ['Square', 'Square', 'Square', 'Square', 'Square', 'Circle', 'Circle', 'Circle', 'Circle', 'Circle', 'Triangle', 'Triangle', 'Triangle', 'Triangle', 'Triangle', 'Diamond', 'Diamond', 'Diamond', 'Diamond', 'Diamond', 'Square+White', 'Square+White', 'Square+White', 'Square+White', 'Square+White', 'Square+White'];
    const shapeColors = ['Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Cyan'];
    
    const shapeType = shapeTypes[pieceId] || 'Unknown'; // Changed from pieceId % shapeTypes.length
    const shapeColor = shapeColors[pieceId] || 'Unknown'; // Changed from pieceId % shapeColors.length
    
    // Debug specific pieces only
    if (pieceId === 0 || pieceId === 17 || pieceId === 25 || pieceId === 1 || pieceId === 11) {
      console.log(`ðŸ” ${shapeColor} ${shapeType} Piece ${pieceId} at position [${position.join(', ')}]`);
      console.log(`  Stored Colors:`, colors);
      
      // Show what colors should be visible based on position
      const [x, y, z] = position;
      const visibleFaces = [];
      if (x === 1) visibleFaces.push('right');
      if (x === -1) visibleFaces.push('left');
      if (y === 1) visibleFaces.push('top');
      if (y === -1) visibleFaces.push('bottom');
      if (z === 1) visibleFaces.push('front');
      if (z === -1) visibleFaces.push('back');
      
      console.log(`  Visible faces:`, visibleFaces);
      console.log(`  Colors on visible faces:`, visibleFaces.map(face => `${face}: ${colors[face]}`));
    }
  }, [pieceId, position, colors]);

  // Color mapping
  const colorMap = {
    'white': '#FFFFFF',
    'yellow': '#FFD700',
    'red': '#FF0000',
    'orange': '#FFA500',
    'blue': '#0000FF',
    'green': '#00FF00',
    'black': '#000000'
  };

  // Create cube geometry
  const cubeGeometry = useMemo(() => new THREE.BoxGeometry(size, size, size), [size]);
  
  // Create face geometry
  const faceGeometry = useMemo(() => {
    const geometry = new THREE.PlaneGeometry(size, size);
    return geometry;
  }, [size]);

  // Get the color for a face based on the face index and position
  const getFaceColor = (faceIndex) => {
    const [x, y, z] = position;
    
    // Map face index to color property
    let faceColor;
    switch (faceIndex) {
      case 0: faceColor = colors.front; break;  // Front face
      case 1: faceColor = colors.back; break;   // Back face
      case 2: faceColor = colors.right; break;  // Right face
      case 3: faceColor = colors.left; break;   // Left face
      case 4: faceColor = colors.top; break;    // Top face
      case 5: faceColor = colors.bottom; break; // Bottom face
      default: faceColor = 'black';
    }
    
    // Determine which faces are visible based on current position
    const visibleFaces = [];
    if (x === 1) visibleFaces.push(2); // Right face visible
    if (x === -1) visibleFaces.push(3); // Left face visible
    if (y === 1) visibleFaces.push(4); // Top face visible
    if (y === -1) visibleFaces.push(5); // Bottom face visible
    if (z === 1) visibleFaces.push(0); // Front face visible
    if (z === -1) visibleFaces.push(1); // Back face visible
    
    // If this face is not visible from current position, return black
    if (!visibleFaces.includes(faceIndex)) {
      return 'black';
    }
    
    // Debug logging for specific pieces
    if (pieceId === 2 || pieceId === 11 || pieceId === 16 || pieceId === 19 || pieceId === 22 || pieceId === 25) {
      const faceNames = ['front', 'back', 'right', 'left', 'top', 'bottom'];
      console.log(`ðŸŽ¨ Piece ${pieceId} face ${faceNames[faceIndex]} (${faceIndex}) at [${x},${y},${z}]: ${faceColor} (visible: ${visibleFaces.includes(faceIndex)})`);
    }
    
    return faceColor;
  };

  return (
    <group position={position}>
      {/* Solid cube base */}
      <mesh geometry={cubeGeometry}>
        <meshPhongMaterial color="#333333" />
      </mesh>
      
      {/* Colored faces */}
      {[0, 1, 2, 3, 4, 5].map((faceIndex) => {
        const faceColor = getFaceColor(faceIndex);
        
        // Define face positions and rotations correctly
        let facePosition, faceRotation;
        
        switch (faceIndex) {
          case 0: // Front face (Z+)
            facePosition = [0, 0, size/2 + 0.001];
            faceRotation = [0, 0, 0];
            break;
          case 1: // Back face (Z-)
            facePosition = [0, 0, -size/2 - 0.001];
            faceRotation = [0, Math.PI, 0];
            break;
          case 2: // Right face (X+)
            facePosition = [size/2 + 0.001, 0, 0];
            faceRotation = [0, Math.PI/2, 0];
            break;
          case 3: // Left face (X-)
            facePosition = [-size/2 - 0.001, 0, 0];
            faceRotation = [0, -Math.PI/2, 0];
            break;
          case 4: // Top face (Y+)
            facePosition = [0, size/2 + 0.001, 0];
            faceRotation = [-Math.PI/2, 0, 0];
            break;
          case 5: // Bottom face (Y-)
            facePosition = [0, -size/2 - 0.001, 0];
            faceRotation = [Math.PI/2, 0, 0];
            break;
          default:
            facePosition = [0, 0, 0];
            faceRotation = [0, 0, 0];
        }
        
        return (
          <mesh
            key={faceIndex}
            position={facePosition}
            rotation={faceRotation}
            geometry={faceGeometry}
          >
            <meshPhongMaterial 
              color={colorMap[faceColor] || '#000000'} 
              side={THREE.FrontSide}
              emissive={colorMap[faceColor] || '#000000'}
              emissiveIntensity={0.3}
            />
          </mesh>
        );
      })}
      
      {/* Debug shape on all faces */}
      {[0, 1, 2, 3, 4, 5].map((faceIndex) => {
        // Define face positions and rotations correctly (same as colored faces)
        let facePosition, faceRotation;
        
        switch (faceIndex) {
          case 0: // Front face (Z+)
            facePosition = [0, 0, size/2 + 0.002];
            faceRotation = [0, 0, 0];
            break;
          case 1: // Back face (Z-)
            facePosition = [0, 0, -size/2 - 0.002];
            faceRotation = [0, Math.PI, 0];
            break;
          case 2: // Right face (X+)
            facePosition = [size/2 + 0.002, 0, 0];
            faceRotation = [0, Math.PI/2, 0];
            break;
          case 3: // Left face (X-)
            facePosition = [-size/2 - 0.002, 0, 0];
            faceRotation = [0, -Math.PI/2, 0];
            break;
          case 4: // Top face (Y+)
            facePosition = [0, size/2 + 0.002, 0];
            faceRotation = [-Math.PI/2, 0, 0];
            break;
          case 5: // Bottom face (Y-)
            facePosition = [0, -size/2 - 0.002, 0];
            faceRotation = [Math.PI/2, 0, 0];
            break;
          default:
            facePosition = [0, 0, 0];
            faceRotation = [0, 0, 0];
        }
        
        return (
          <group key={`shape-${faceIndex}`} position={facePosition} rotation={faceRotation}>
            {createShape(pieceId, size * 0.3)}
          </group>
        );
      })}
    </group>
  );
}

// Function to get the original solved colors for a piece based on its pieceId
function getOriginalColors(pieceId) {
  // Map pieceId to original solved position
  const positions = [];
  for (let x = -1; x <= 1; x++) {
    for (let y = -1; y <= 1; y++) {
      for (let z = -1; z <= 1; z++) {
        if (x === 0 && y === 0 && z === 0) continue;
        positions.push([x, y, z]);
      }
    }
  }
  
  const originalPosition = positions[pieceId];
  const [x, y, z] = originalPosition;
  
  // Assign colors based on original solved position
  let colors = {
    front: 'black',
    back: 'black', 
    right: 'black',
    left: 'black',
    top: 'black',
    bottom: 'black'
  };
  
  // Only assign colors to faces that are visible (not internal) in solved state
  if (x === -1) colors.left = 'orange';
  if (x === 1) colors.right = 'red';
  if (y === -1) colors.bottom = 'green';
  if (y === 1) colors.top = 'blue';
  if (z === -1) colors.back = 'yellow';
  if (z === 1) colors.front = 'white';
  
  return colors;
}

// Custom hook for cube state management
function useCubeState() {
  const [cubeState, setCubeState] = useState(() => {
    const state = [];
    
    // Create 26 pieces (3x3x3 minus center)
    for (let x = -1; x <= 1; x++) {
      for (let y = -1; y <= 1; y++) {
        for (let z = -1; z <= 1; z++) {
          // Skip the center piece
          if (x === 0 && y === 0 && z === 0) continue;
          
          // Get pieceId based on position in solved state
          const pieceId = state.length;
          
          // Get original colors for this piece
          const colors = getOriginalColors(pieceId);
          
          // Debug logging for initial state
          console.log(`Initial piece ${pieceId} at [${x}, ${y}, ${z}]:`, colors);
          
          state.push({ 
            position: [x, y, z], 
            colors,
            rotationHistory: [], // Track all rotations this piece has undergone
            pieceId: pieceId // Store the pieceId for reference
          });
        }
      }
    }
    
    // Log the complete initial cube state as JSON
    console.log('ðŸŽ¯ INITIAL CUBE STATE (End of Initial Load):');
    console.log(JSON.stringify(state, null, 2));
    
    // Also log to terminal (this will show in the terminal where npm start is running)
    logToTerminal('INITIAL CUBE STATE (End of Initial Load)', state);
    
    return state;
  });

  const [isAnimating, setIsAnimating] = useState(false);

  // Consolidated rotation logic
  const applyRotation = useCallback((pieces, face, direction) => {
    pieces.forEach(piece => {
      const [x, y, z] = piece.position;
      let newX = x, newY = y, newZ = z;
      // Apply 3D transformation
      switch (face) {
        case 'F': // Front face rotation (Z+ plane)
          if (z === 1) { // Only pieces on the front face
            if (direction === 'clockwise') {
              newX = -y;
              newY = x;
            } else {
              newX = y;
              newY = -x;
            }
          }
          break;
        case 'B': // Back face rotation (Z- plane)
          if (z === -1) { // Only pieces on the back face
            if (direction === 'clockwise') {
              newX = y;
              newY = -x;
            } else {
              newX = -y;
              newY = x;
            }
          }
          break;
        case 'R': // Right face rotation (X+ plane)
          if (x === 1) { // Only pieces on the right face
            if (direction === 'clockwise') {
              newY = -z;
              newZ = y;
            } else {
              newY = z;
              newZ = -y;
            }
          }
          break;
        case 'L': // Left face rotation (X- plane)
          if (x === -1) { // Only pieces on the left face
            if (direction === 'clockwise') {
              newY = z;
              newZ = -y;
            } else {
              newY = -z;
              newZ = y;
            }
          }
          break;
        case 'U': // Up face rotation (Y+ plane)
          if (y === 1) { // Only pieces on the top face
            if (direction === 'clockwise') {
              newX = -z;
              newZ = x;
            } else {
              newX = z;
              newZ = -x;
            }
          }
          break;
        case 'D': // Down face rotation (Y- plane)
          if (y === -1) { // Only pieces on the bottom face
            if (direction === 'clockwise') {
              newX = z;
              newZ = -x;
            } else {
              newX = -z;
              newZ = x;
            }
          }
          break;
      }
      
      // Only apply changes if the piece actually moved
      const positionChanged = (x !== newX || y !== newY || z !== newZ);
      
      if (positionChanged) {
        // Update position
        piece.position = [newX, newY, newZ];
        
        // Rotate the colors to match the piece's new orientation
        const newColors = { ...piece.colors };
        
        // Apply color rotation based on the face rotation
        switch (face) {
          case 'F': // Front face rotation
            if (direction === 'clockwise') {
              // Colors rotate: top -> right, right -> bottom, bottom -> left, left -> top
              const temp = newColors.top;
              newColors.top = newColors.left;
              newColors.left = newColors.bottom;
              newColors.bottom = newColors.right;
              newColors.right = temp;
            } else {
              // Colors rotate: top -> left, left -> bottom, bottom -> right, right -> top
              const temp = newColors.top;
              newColors.top = newColors.right;
              newColors.right = newColors.bottom;
              newColors.bottom = newColors.left;
              newColors.left = temp;
            }
            break;
          case 'B': // Back face rotation
            if (direction === 'clockwise') {
              // Colors rotate: top -> left, left -> bottom, bottom -> right, right -> top
              const temp = newColors.top;
              newColors.top = newColors.right;
              newColors.right = newColors.bottom;
              newColors.bottom = newColors.left;
              newColors.left = temp;
            } else {
              // Colors rotate: top -> right, right -> bottom, bottom -> left, left -> top
              const temp = newColors.top;
              newColors.top = newColors.left;
              newColors.left = newColors.bottom;
              newColors.bottom = newColors.right;
              newColors.right = temp;
            }
            break;
          case 'R': // Right face rotation
            if (direction === 'clockwise') {
              // Colors rotate: top -> back, back -> bottom, bottom -> front, front -> top
              const temp = newColors.top;
              newColors.top = newColors.front;
              newColors.front = newColors.bottom;
              newColors.bottom = newColors.back;
              newColors.back = temp;
            } else {
              // Colors rotate: top -> front, front -> bottom, bottom -> back, back -> top
              const temp = newColors.top;
              newColors.top = newColors.back;
              newColors.back = newColors.bottom;
              newColors.bottom = newColors.front;
              newColors.front = temp;
            }
            break;
          case 'L': // Left face rotation
            if (direction === 'clockwise') {
              // Colors rotate: top -> front, front -> bottom, bottom -> back, back -> top
              const temp = newColors.top;
              newColors.top = newColors.back;
              newColors.back = newColors.bottom;
              newColors.bottom = newColors.front;
              newColors.front = temp;
            } else {
              // Colors rotate: top -> back, back -> bottom, bottom -> front, front -> top
              const temp = newColors.top;
              newColors.top = newColors.front;
              newColors.front = newColors.bottom;
              newColors.bottom = newColors.back;
              newColors.back = temp;
            }
            break;
          case 'U': // Up face rotation
            if (direction === 'clockwise') {
              // Colors rotate: front -> right, right -> back, back -> left, left -> front
              const temp = newColors.front;
              newColors.front = newColors.left;
              newColors.left = newColors.back;
              newColors.back = newColors.right;
              newColors.right = temp;
            } else {
              // Colors rotate: front -> left, left -> back, back -> right, right -> front
              const temp = newColors.front;
              newColors.front = newColors.right;
              newColors.right = newColors.back;
              newColors.back = newColors.left;
              newColors.left = temp;
            }
            break;
          case 'D': // Down face rotation
            if (direction === 'clockwise') {
              // Colors rotate: front -> left, left -> back, back -> right, right -> front
              const temp = newColors.front;
              newColors.front = newColors.right;
              newColors.right = newColors.back;
              newColors.back = newColors.left;
              newColors.left = temp;
            } else {
              // Colors rotate: front -> right, right -> back, back -> left, left -> front
              const temp = newColors.front;
              newColors.front = newColors.left;
              newColors.left = newColors.back;
              newColors.back = newColors.right;
              newColors.right = temp;
            }
            break;
        }
        
        // Update the piece's colors (maintain original colors, just rotate them)
        piece.colors = newColors;
        
        piece.rotationHistory.push({
          move: face,
          direction: direction,
          fromPosition: [x, y, z],
          toPosition: [newX, newY, newZ],
          timestamp: new Date().toISOString()
        });
      }
    });
  }, []);

  // Rotate a face of the cube
  const rotateFace = useCallback((face, direction) => {
    if (isAnimating) return;
    
    setIsAnimating(true);
    
    setCubeState(prevState => {
      const newState = [...prevState];
      const pieces = newState.map((piece, index) => ({ ...piece, pieceId: index }));
      
      // Apply rotation using consolidated logic
      applyRotation(pieces, face, direction);

      // Update the newState with the modified pieces
      pieces.forEach((piece, index) => {
        newState[index] = {
          position: piece.position,
          colors: piece.colors,
          rotationHistory: piece.rotationHistory
        };
      });
      
      return newState;
    });
    
    // Reset animation state after a short delay
    setTimeout(() => setIsAnimating(false), 100);
  }, [isAnimating, applyRotation]);

  // Execute scramble sequence
  const executeScramble = useCallback((sequence) => {
    if (sequence.length === 0) {
      setIsAnimating(false);
      
      // Log the complete cube state as JSON at the end of scramble session
      // We need to get the current state from the setter function
      setCubeState(currentState => {
        console.log('ðŸŽ¯ CUBE STATE AFTER SCRAMBLE (End of Scramble Session):');
        console.log(JSON.stringify(currentState, null, 2));
        
        // Also log to terminal (this will show in the terminal where npm start is running)
        logToTerminal('CUBE STATE AFTER SCRAMBLE (End of Scramble Session)', currentState);
        
        return currentState; // Return unchanged state
      });
      
      return;
    }
    
    const { move, direction } = sequence[0];
    console.log(`Executing move: ${move} ${direction}`);
    
    // Apply the move using consolidated logic
    setCubeState(prevState => {
      const newState = [...prevState];
      const pieces = newState.map((piece, index) => ({ ...piece, pieceId: index }));
      
      // Apply rotation using consolidated logic
      applyRotation(pieces, move, direction);

      // Update the newState with the modified pieces
      pieces.forEach((piece, index) => {
        newState[index] = {
          position: piece.position,
          colors: piece.colors,
          rotationHistory: piece.rotationHistory
        };
      });
      
      return newState;
    });
    
    // Continue with next move after a short delay
    setTimeout(() => executeScramble(sequence.slice(1)), 100);
  }, [applyRotation]);

  // Scramble the cube
  const scramble = useCallback(() => {
    if (isAnimating) return;
    
    setIsAnimating(true);
    
    const moves = ['F', 'B', 'R', 'L', 'U', 'D'];
    const directions = ['clockwise', 'counterclockwise'];
    const scrambleSequence = [];
    
    // Generate 20 random moves
    for (let i = 0; i < 20; i++) {
      const move = moves[Math.floor(Math.random() * moves.length)];
      const direction = directions[Math.floor(Math.random() * directions.length)];
      scrambleSequence.push({ move, direction });
    }
    
    console.log('Scrambling with sequence:', scrambleSequence);
    
    // Execute scramble moves
    executeScramble(scrambleSequence);
  }, [isAnimating, executeScramble]);

  // Reset the cube to solved state
  const reset = useCallback(() => {
    setCubeState(() => {
      const state = [];
      
      // Create 26 pieces (3x3x3 minus center)
      for (let x = -1; x <= 1; x++) {
        for (let y = -1; y <= 1; y++) {
          for (let z = -1; z <= 1; z++) {
            // Skip the center piece
            if (x === 0 && y === 0 && z === 0) continue;
            
            // Get pieceId based on position in solved state
            const pieceId = state.length;
            
            // Get original colors for this piece
            const colors = getOriginalColors(pieceId);
            
            // Debug logging for reset state
            console.log(`Reset piece ${pieceId} at [${x}, ${y}, ${z}]:`, colors);
            
            state.push({ 
              position: [x, y, z], 
              colors,
              rotationHistory: [], // Reset rotation history
              pieceId: pieceId // Store the pieceId for reference
            });
          }
        }
      }
      
      return state;
    });
  }, []);

  return {
    cubeState,
    isAnimating,
    rotateFace,
    scramble,
    reset
  };
}

// Main cube group component with enhanced structure
function CubeGroup({ isRotating, autoRotate = false, onScramble, onReset, onCubeStateChange }) {
  const groupRef = useRef();
  const [rotationSpeed] = useState({ x: 0.005, y: 0.01 });
  
  const { cubeState, isAnimating, rotateFace, scramble, reset } = useCubeState();
  
  // Helper functions for enhanced debugging
  const getShapeType = (pieceId) => {
    const shapeTypes = ['Square', 'Square', 'Square', 'Square', 'Square', 'Circle', 'Circle', 'Circle', 'Circle', 'Circle', 'Triangle', 'Triangle', 'Triangle', 'Triangle', 'Diamond', 'Diamond', 'Diamond', 'Diamond', 'Diamond', 'Diamond', 'Square+White', 'Square+White', 'Square+White', 'Square+White', 'Square+White', 'Square+White'];
    return shapeTypes[pieceId] || 'Unknown';
  };

  const getShapeColor = (pieceId) => {
    const shapeColors = ['Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Yellow', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Yellow'];
    return shapeColors[pieceId] || 'Unknown';
  };

  const getExpectedPosition = (pieceId) => {
    // Map pieceId to expected solved position
    const positions = [];
    for (let x = -1; x <= 1; x++) {
      for (let y = -1; y <= 1; y++) {
        for (let z = -1; z <= 1; z++) {
          if (x === 0 && y === 0 && z === 0) continue;
          positions.push([x, y, z]);
        }
      }
    }
    return positions[pieceId];
  };

  const getExpectedColorsForPosition = (position) => {
    const [x, y, z] = position;
    const expectedColors = {
      front: 'black',
      back: 'black',
      right: 'black',
      left: 'black',
      top: 'black',
      bottom: 'black'
    };
    
    if (x === -1) expectedColors.left = 'orange';
    if (x === 1) expectedColors.right = 'red';
    if (y === -1) expectedColors.bottom = 'green';
    if (y === 1) expectedColors.top = 'blue';
    if (z === -1) expectedColors.back = 'yellow';
    if (z === 1) expectedColors.front = 'white';
    
    return expectedColors;
  };

  const isPieceInCorrectPosition = (piece, pieceId) => {
    // Check if piece is in its solved position
    const [x, y, z] = piece.position;
    const expectedPosition = getExpectedPosition(pieceId);
    return x === expectedPosition[0] && y === expectedPosition[1] && z === expectedPosition[2];
  };

  const getColorMismatches = (piece) => {
    // In a scrambled cube, there are no color mismatches based on position
    // Colors should only be validated against the solved state, not current position
    // This function is mainly for debugging and should not flag scrambled pieces as incorrect
    return [];
  };

  const getVisibleFaces = (position) => {
    const [x, y, z] = position;
    const visibleFaces = [];
    if (x === 1) visibleFaces.push('right');
    if (x === -1) visibleFaces.push('left');
    if (y === 1) visibleFaces.push('top');
    if (y === -1) visibleFaces.push('bottom');
    if (z === 1) visibleFaces.push('front');
    if (z === -1) visibleFaces.push('back');
    return visibleFaces;
  };

  const getVisibleColors = (piece) => {
    const visibleFaces = getVisibleFaces(piece.position);
    return visibleFaces.map(face => ({
      face,
      color: piece.colors[face]
    }));
  };

  const isCubeSolved = (state) => {
    return state.every((piece, index) => isPieceInCorrectPosition(piece, index));
  };
  
  // Expose functions to parent
  React.useEffect(() => {
    if (onScramble) onScramble(scramble);
    if (onReset) onReset(reset);
  }, [onScramble, onReset, scramble, reset]);
  
  // Notify parent of cube state changes with enhanced debugging info
  React.useEffect(() => {
    if (onCubeStateChange && cubeState) {
      console.log('ðŸ” Cube state changed, pieces count:', cubeState.length);
      console.log('ðŸ” First few pieces:', cubeState.slice(0, 3));
      
      // Create enhanced cube state with debugging information
      const enhancedCubeState = {
        timestamp: new Date().toISOString(),
        totalPieces: cubeState.length,
        pieces: cubeState.map((piece, index) => ({
          pieceId: index,
          position: piece.position,
          colors: piece.colors,
          rotationHistory: piece.rotationHistory,
          shapeType: getShapeType(index),
          shapeColor: getShapeColor(index),
          // Validation data
          validation: {
            isInCorrectPosition: isPieceInCorrectPosition(piece, index),
            expectedColors: getExpectedColorsForPosition(piece.position),
            colorMismatches: getColorMismatches(piece),
            visibleFaces: getVisibleFaces(piece.position),
            visibleColors: getVisibleColors(piece)
          },
          // Debugging helpers
          debug: {
            positionString: `[${piece.position.join(', ')}]`,
            colorSummary: Object.entries(piece.colors)
              .filter(([_, color]) => color !== 'black')
              .map(([face, color]) => `${face}:${color}`)
              .join(', '),
            moveCount: piece.rotationHistory.length,
            lastMove: piece.rotationHistory[piece.rotationHistory.length - 1] || null
          }
        })),
        // Global validation
        validation: {
          isSolved: isCubeSolved(cubeState),
          totalMoves: cubeState.reduce((sum, piece) => sum + piece.rotationHistory.length, 0),
          piecesInCorrectPosition: cubeState.filter((piece, index) => 
            isPieceInCorrectPosition(piece, index)
          ).length,
          piecesWithColorMismatches: 0, // No color mismatches in scrambled state
          isScrambled: !isCubeSolved(cubeState) && cubeState.some(piece => piece.rotationHistory.length > 0)
        },
        // Metadata
        metadata: {
          version: "1.0.0",
          exportReason: "debugging",
          cubeType: "3x3x3",
          coordinateSystem: "right-handed (X-right, Y-up, Z-front)",
          faceMapping: {
            front: "Z+ (white)",
            back: "Z- (yellow)", 
            right: "X+ (red)",
            left: "X- (orange)",
            top: "Y+ (blue)",
            bottom: "Y- (green)"
          },
          shapeMapping: {
            "0-4": "Squares (Red, Blue, Green, Orange, Purple)",
            "5-9": "Circles (Red, Blue, Green, Orange, Purple)", 
            "10-14": "Triangles (Red, Blue, Green, Orange, Purple)",
            "15-19": "Diamonds (Red, Blue, Green, Orange, Purple)",
            "20-25": "Squares+White (Red, Blue, Green, Orange, Purple, Cyan)"
          }
        }
      };
      
      onCubeStateChange(enhancedCubeState);
    }
  }, [onCubeStateChange, cubeState]);

  // Auto-rotation animation
  useFrame(() => {
    if (groupRef.current && isRotating && !isAnimating) {
      groupRef.current.rotation.x += rotationSpeed.x;
      groupRef.current.rotation.y += rotationSpeed.y;
    }
  });

  return (
    <group ref={groupRef}>
      {cubeState.map((piece, index) => (
        <CubePiece
          key={index}
          position={piece.position}
          colors={piece.colors}
          pieceId={index}
        />
      ))}
    </group>
  );
}

// Main Rubik's Cube component
function RubiksCube({ isRotating, autoRotate = false, onScramble, onReset, onCubeStateChange }) {
  const groupRef = useRef();
  const [rotationSpeed] = useState({ x: 0.005, y: 0.01 });
  
  const { cubeState, isAnimating, rotateFace, scramble, reset } = useCubeState();
  
  // Expose functions to parent
  React.useEffect(() => {
    if (onScramble) onScramble(scramble);
    if (onReset) onReset(reset);
  }, [onScramble, onReset, scramble, reset]);
  
  // Notify parent of cube state changes
  React.useEffect(() => {
    if (onCubeStateChange && cubeState) {
      onCubeStateChange(cubeState);
    }
  }, [onCubeStateChange, cubeState]);

  return (
    <Canvas
      camera={{ position: [5, 5, 5], fov: 50 }}
      style={{ width: '100%', height: '100%' }}
    >
      {/* Enhanced lighting setup - dimmed for better color distinction */}
      <ambientLight intensity={0.8} />
      <directionalLight position={[10, 10, 5]} intensity={2.0} />
      <pointLight position={[10, 10, 10]} intensity={1.5} />
      <pointLight position={[-10, -10, -10]} intensity={1.5} />
      <spotLight
        position={[0, 10, 0]}
        angle={0.3}
        penumbra={1}
        intensity={1.5}
        castShadow
      />
      
      {/* Environment for better lighting */}
      <Environment preset="sunset" />
      
      <CubeGroup 
        isRotating={isRotating} 
        autoRotate={autoRotate}
        onScramble={onScramble}
        onReset={onReset}
        onCubeStateChange={onCubeStateChange}
      />
      
      <OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />
    </Canvas>
  );
}

export default RubiksCube;
