import React, { useRef, useMemo, useState, useCallback } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import * as THREE from 'three';

// Individual cube piece component with proper multi-colored faces
function CubePiece({ position, colors, size = 0.95 }) {
  const meshRef = useRef();
  
  const colorMap = {
    'white': '#ffffff',
    'yellow': '#ffd700',
    'red': '#ff0000',
    'orange': '#ff8c00',
    'blue': '#0000ff',
    'green': '#00ff00',
    'black': '#1a1a1a'
  };

        // Create individual face geometries for each side
        const faceGeometry = useMemo(() => {
          const geometry = new THREE.PlaneGeometry(size, size);
          // Don't rotate - keep faces facing outward by default
          return geometry;
        }, [size]);

  // Get the color for a face based on the piece's current position and orientation
  const getFaceColor = (faceIndex) => {
    const [x, y, z] = position;
    
    // Face indices: 0=front, 1=back, 2=right, 3=left, 4=top, 5=bottom
    // Determine which face should be visible based on current position
    switch (faceIndex) {
      case 0: // Front face - show the color that should be on the front
        if (z === 1) return colors.front;
        if (z === -1) return colors.back;
        if (x === 1) return colors.right;
        if (x === -1) return colors.left;
        if (y === 1) return colors.top;
        if (y === -1) return colors.bottom;
        return 'black';
      case 1: // Back face - show the color that should be on the back
        if (z === -1) return colors.front;
        if (z === 1) return colors.back;
        if (x === -1) return colors.right;
        if (x === 1) return colors.left;
        if (y === 1) return colors.top;
        if (y === -1) return colors.bottom;
        return 'black';
      case 2: // Right face - show the color that should be on the right
        if (x === 1) return colors.front;
        if (x === -1) return colors.back;
        if (y === 1) return colors.right;
        if (y === -1) return colors.left;
        if (z === 1) return colors.top;
        if (z === -1) return colors.bottom;
        return 'black';
      case 3: // Left face - show the color that should be on the left
        if (x === -1) return colors.front;
        if (x === 1) return colors.back;
        if (y === -1) return colors.right;
        if (y === 1) return colors.left;
        if (z === -1) return colors.top;
        if (z === 1) return colors.bottom;
        return 'black';
      case 4: // Top face - show the color that should be on the top
        if (y === 1) return colors.front;
        if (y === -1) return colors.back;
        if (z === 1) return colors.right;
        if (z === -1) return colors.left;
        if (x === 1) return colors.top;
        if (x === -1) return colors.bottom;
        return 'black';
      case 5: // Bottom face - show the color that should be on the bottom
        if (y === -1) return colors.front;
        if (y === 1) return colors.back;
        if (z === -1) return colors.right;
        if (z === 1) return colors.left;
        if (x === -1) return colors.top;
        if (x === 1) return colors.bottom;
        return 'black';
      default: return 'black';
    }
  };

  // Face positions and rotations for a cube - faces positioned on the outside
  const faces = [
    { position: [0, 0, size/2 + 0.01], rotation: [0, 0, 0], color: getFaceColor(0) }, // Front
    { position: [0, 0, -size/2 - 0.01], rotation: [0, Math.PI, 0], color: getFaceColor(1) }, // Back
    { position: [size/2 + 0.01, 0, 0], rotation: [0, Math.PI/2, 0], color: getFaceColor(2) }, // Right
    { position: [-size/2 - 0.01, 0, 0], rotation: [0, -Math.PI/2, 0], color: getFaceColor(3) }, // Left
    { position: [0, size/2 + 0.01, 0], rotation: [-Math.PI/2, 0, 0], color: getFaceColor(4) }, // Top
    { position: [0, -size/2 - 0.01, 0], rotation: [Math.PI/2, 0, 0], color: getFaceColor(5) }  // Bottom
  ];

  return (
    <group ref={meshRef} position={position}>
      {faces.map((face, index) => (
        <mesh key={index} position={face.position} rotation={face.rotation}>
          <primitive object={faceGeometry} />
          <meshPhongMaterial 
            color={colorMap[face.color] || colorMap['black']}
            side={THREE.DoubleSide}
            shininess={100}
            specular={0x222222}
            emissive={colorMap[face.color] || colorMap['black']}
            emissiveIntensity={0.5}
          />
        </mesh>
      ))}
    </group>
  );
}

