      return newState;
    });
    
    // Animation delay
    setTimeout(() => setIsAnimating(false), 300);
  }, [isAnimating]);

  // Execute scramble moves
  const executeScramble = useCallback((queue) => {
    if (queue.length === 0) {
      setIsAnimating(false);
      return;
    }
    
    const { move, direction } = queue[0];
    console.log(`Executing move: ${move} ${direction}`);
    
    // Apply the rotation immediately
    setCubeState(prevState => {
      const newState = [...prevState];
      
