        'B': newState.filter(p => p.position[2] === -1), // Back face
        'R': newState.filter(p => p.position[0] === 1), // Right face
        'L': newState.filter(p => p.position[0] === -1), // Left face
        'U': newState.filter(p => p.position[1] === 1), // Up face
        'D': newState.filter(p => p.position[1] === -1) // Down face
      };

      const pieces = facePieces[move];
      if (!pieces) return prevState;

      // Apply rotation based on face and direction
      pieces.forEach(piece => {
        const [x, y, z] = piece.position;
        let newX = x, newY = y, newZ = z;
        
        switch (move) {
          case 'F': // Front face rotation
            if (direction === 'clockwise') {
              newX = -y;
              newY = x;
            } else {
              newX = y;
              newY = -x;
            }
            break;
          case 'B': // Back face rotation
            if (direction === 'clockwise') {
              newX = y;
              newY = -x;
            } else {
              newX = -y;
              newY = x;
            }
            break;
          case 'R': // Right face rotation
            if (direction === 'clockwise') {
              newZ = -y;
              newY = z;
            } else {
              newZ = y;
              newY = -z;
            }
            break;
          case 'L': // Left face rotation
            if (direction === 'clockwise') {
              newZ = y;
              newY = -z;
            } else {
              newZ = -y;
              newY = z;
            }
            break;
          case 'U': // Up face rotation
            if (direction === 'clockwise') {
              newX = z;
              newZ = -x;
            } else {
              newX = -z;
              newZ = x;
            }
            break;
          case 'D': // Down face rotation
            if (direction === 'clockwise') {
              newX = -z;
              newZ = x;
            } else {
              newX = z;
              newZ = -x;
            }
            break;
        }
        
        piece.position = [newX, newY, newZ];
      });

      return newState;
    });
    
    // Continue with the next move after a delay
    setTimeout(() => {
      executeScramble(queue.slice(1));
    }, 350);
  }, []);

  // Scramble function using standard notation
  const scramble = useCallback(() => {
    console.log('Scramble function called');
    if (isAnimating) {
      console.log('Already animating, skipping scramble');
      return;
    }
    
    setIsAnimating(true);
    console.log('Starting scramble animation');
    
    const moves = ['F', 'B', 'R', 'L', 'U', 'D'];
    const directions = ['clockwise', 'counterclockwise'];
    const scrambleLength = 20;
    
    let currentQueue = [];
    let lastMove = '';
    
    for (let i = 0; i < scrambleLength; i++) {
      let move;
      let direction;
      
      // Avoid doing the same move twice in a row
      do {
        move = moves[Math.floor(Math.random() * moves.length)];
      } while (move === lastMove);
      
      direction = directions[Math.floor(Math.random() * directions.length)];
      lastMove = move;
      
      currentQueue.push({ move, direction });
    }
    
    console.log('Generated scramble queue:', currentQueue);
    setAnimationQueue(currentQueue);
    executeScramble(currentQueue);
  }, [isAnimating, executeScramble]);

  // Reset function
  const reset = useCallback(() => {
    if (isAnimating) return;
    
    setCubeState(() => {
      const state = [];
      for (let x = -1; x <= 1; x++) {
        for (let y = -1; y <= 1; y++) {
          for (let z = -1; z <= 1; z++) {
            // Skip the center piece (0, 0, 0) - this is the core mechanism
            if (x === 0 && y === 0 && z === 0) continue;
            
            const position = [x, y, z];
            // Store original colors for each face based on solved state
            // Each piece gets colors based on its position type (corner, edge, center)
            const colors = {
