import React, { useRef, useMemo, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import * as THREE from 'three';

// Individual cube piece component with improved materials
function CubePiece({ position, colors, size = 0.95 }) {
  const meshRef = useRef();
  
  const geometry = useMemo(() => new THREE.BoxGeometry(size, size, size), [size]);
  
  const materials = useMemo(() => {
    const materialArray = [];
    const colorMap = {
      'white': '#ffffff',
      'yellow': '#ffff00',
      'red': '#ff0000',
      'orange': '#ff8c00',
      'blue': '#0000ff',
      'green': '#00ff00',
      'black': '#1a1a1a'
    };
    
    // Three.js BoxGeometry face order: [right, left, top, bottom, front, back]
    // Our colors array: [front, back, right, left, top, bottom]
    // Map our colors to Three.js face order correctly
    // Three.js faces: 0=right, 1=left, 2=top, 3=bottom, 4=front, 5=back
    const faceOrder = [4, 5, 0, 1, 2, 3]; // Map our colors to Three.js face order
    
    for (let i = 0; i < 6; i++) {
      const colorIndex = faceOrder[i];
      const color = colors[colorIndex] || 'black';
      materialArray.push(
        new THREE.MeshPhongMaterial({ 
          color: colorMap[color],
          transparent: true,
          opacity: color === 'black' ? 0.1 : 1,
          shininess: color === 'black' ? 0 : 30,
          specular: color === 'black' ? 0x000000 : 0x444444
        })
      );
    }
    return materialArray;
  }, [colors]);

  return (
    <mesh ref={meshRef} position={position} geometry={geometry} material={materials} />
  );
}

// Main cube group component with enhanced structure
function CubeGroup({ isRotating, autoRotate }) {
  const groupRef = useRef();
  const [rotationSpeed] = useState({ x: 0.005, y: 0.01 });
  
  useFrame((state) => {
    if (autoRotate && groupRef.current) {
      groupRef.current.rotation.y += rotationSpeed.y;
      groupRef.current.rotation.x += rotationSpeed.x;
    }
  });

  // Generate complete 3x3x3 cube structure
  const cubePieces = useMemo(() => {
    const pieces = [];
    
    // Create all 27 pieces (3x3x3)
    for (let x = -1; x <= 1; x++) {
      for (let y = -1; y <= 1; y++) {
        for (let z = -1; z <= 1; z++) {
          const position = [x * 1.1, y * 1.1, z * 1.1];
          
          // Determine colors for each face based on position
          const pieceColors = ['black', 'black', 'black', 'black', 'black', 'black'];
          
          // Front face (z = 1) - white
          if (z === 1) pieceColors[0] = 'white';
          // Back face (z = -1) - yellow  
          if (z === -1) pieceColors[1] = 'yellow';
          // Right face (x = 1) - red
          if (x === 1) pieceColors[2] = 'red';
          // Left face (x = -1) - orange
          if (x === -1) pieceColors[3] = 'orange';
          // Top face (y = 1) - blue
          if (y === 1) pieceColors[4] = 'blue';
          // Bottom face (y = -1) - green
          if (y === -1) pieceColors[5] = 'green';
          
          pieces.push({
            position,
            colors: pieceColors
          });
        }
      }
    }
    
    return pieces;
  }, []);

  return (
    <group ref={groupRef}>
      {cubePieces.map((piece, index) => (
        <CubePiece
          key={index}
          position={piece.position}
          colors={piece.colors}
        />
      ))}
    </group>
  );
}

// Main Rubik's Cube component
function RubiksCube({ isRotating, autoRotate }) {
  return (
    <Canvas
      camera={{ position: [6, 6, 6], fov: 60 }}
      style={{ width: '100%', height: '100%' }}
      gl={{ antialias: true, alpha: true }}
    >
      {/* Enhanced Lighting */}
      <ambientLight intensity={0.3} />
      <directionalLight 
        position={[10, 10, 5]} 
        intensity={1.2}
        castShadow
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
      />
      <pointLight position={[-10, -10, -5]} intensity={0.8} />
      <spotLight
        position={[0, 10, 0]}
        angle={0.3}
        penumbra={1}
        intensity={0.5}
        castShadow
      />
      
      {/* Environment */}
      <Environment preset="city" />
      
      {/* Cube */}
      <CubeGroup isRotating={isRotating} autoRotate={autoRotate} />
      
      {/* Controls */}
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        autoRotate={false}
        autoRotateSpeed={1}
        minDistance={3}
        maxDistance={15}
        dampingFactor={0.05}
        enableDamping={true}
      />
    </Canvas>
  );
}

export default RubiksCube;
