import React, { useRef, useMemo, useState } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import * as THREE from 'three';

// Individual cube piece component with improved materials
function CubePiece({ position, colors, size = 0.95 }) {
  const meshRef = useRef();
  
  const colorMap = {
    'white': '#ffffff',
    'yellow': '#ffff00',
    'red': '#ff0000',
    'orange': '#ff8c00',
    'blue': '#0000ff',
    'green': '#00ff00',
    'black': '#1a1a1a'
  };

  // Create individual face meshes for each side
  const faceSize = size * 0.5;
  const faceGeometry = useMemo(() => new THREE.PlaneGeometry(faceSize, faceSize), [faceSize]);

  return (
    <group ref={meshRef} position={position}>
             {/* Front face (z = 1) */}
       <mesh position={[0, 0, size * 0.5]} rotation={[0, 0, 0]}>
         <primitive object={faceGeometry} />
         <meshPhongMaterial 
           color={colorMap[colors[0]] || colorMap['black']}
           transparent={true}
           opacity={colors[0] === 'black' ? 0.1 : 1}
           side={THREE.DoubleSide}
         />
       </mesh>
       
       {/* Back face (z = -1) */}
       <mesh position={[0, 0, -size * 0.5]} rotation={[0, Math.PI, 0]}>
         <primitive object={faceGeometry} />
         <meshPhongMaterial 
           color={colorMap[colors[1]] || colorMap['black']}
           transparent={true}
           opacity={colors[1] === 'black' ? 0.1 : 1}
           side={THREE.DoubleSide}
         />
       </mesh>
       
       {/* Right face (x = 1) */}
       <mesh position={[size * 0.5, 0, 0]} rotation={[0, Math.PI / 2, 0]}>
         <primitive object={faceGeometry} />
         <meshPhongMaterial 
           color={colorMap[colors[2]] || colorMap['black']}
           transparent={true}
           opacity={colors[2] === 'black' ? 0.1 : 1}
           side={THREE.DoubleSide}
         />
       </mesh>
       
       {/* Left face (x = -1) */}
       <mesh position={[-size * 0.5, 0, 0]} rotation={[0, -Math.PI / 2, 0]}>
         <primitive object={faceGeometry} />
         <meshPhongMaterial 
           color={colorMap[colors[3]] || colorMap['black']}
           transparent={true}
           opacity={colors[3] === 'black' ? 0.1 : 1}
           side={THREE.DoubleSide}
         />
       </mesh>
       
       {/* Top face (y = 1) */}
       <mesh position={[0, size * 0.5, 0]} rotation={[-Math.PI / 2, 0, 0]}>
         <primitive object={faceGeometry} />
         <meshPhongMaterial 
           color={colorMap[colors[4]] || colorMap['black']}
           transparent={true}
           opacity={colors[4] === 'black' ? 0.1 : 1}
           side={THREE.DoubleSide}
         />
       </mesh>
       
       {/* Bottom face (y = -1) */}
       <mesh position={[0, -size * 0.5, 0]} rotation={[Math.PI / 2, 0, 0]}>
         <primitive object={faceGeometry} />
         <meshPhongMaterial 
           color={colorMap[colors[5]] || colorMap['black']}
           transparent={true}
           opacity={colors[5] === 'black' ? 0.1 : 1}
           side={THREE.DoubleSide}
         />
       </mesh>
    </group>
  );
}

// Main cube group component with enhanced structure
function CubeGroup({ isRotating, autoRotate }) {
  const groupRef = useRef();
  const [rotationSpeed] = useState({ x: 0.005, y: 0.01 });
  
  useFrame((state) => {
    if (autoRotate && groupRef.current) {
      groupRef.current.rotation.y += rotationSpeed.y;
      groupRef.current.rotation.x += rotationSpeed.x;
    }
  });

  // Generate complete 3x3x3 cube structure
  const cubePieces = useMemo(() => {
    const pieces = [];
    
    // Create all 27 pieces (3x3x3)
    for (let x = -1; x <= 1; x++) {
      for (let y = -1; y <= 1; y++) {
        for (let z = -1; z <= 1; z++) {
          const position = [x * 1.1, y * 1.1, z * 1.1];
          
          // Determine colors for each face based on position
          const pieceColors = ['black', 'black', 'black', 'black', 'black', 'black'];
          
          // Front face (z = 1) - white
          if (z === 1) pieceColors[0] = 'white';
          // Back face (z = -1) - yellow  
          if (z === -1) pieceColors[1] = 'yellow';
          // Right face (x = 1) - red
          if (x === 1) pieceColors[2] = 'red';
          // Left face (x = -1) - orange
          if (x === -1) pieceColors[3] = 'orange';
          // Top face (y = 1) - blue
          if (y === 1) pieceColors[4] = 'blue';
          // Bottom face (y = -1) - green
          if (y === -1) pieceColors[5] = 'green';
          
          pieces.push({
            position,
            colors: pieceColors
          });
        }
      }
    }
    
    return pieces;
  }, []);

  return (
    <group ref={groupRef}>
      {cubePieces.map((piece, index) => (
        <CubePiece
          key={index}
          position={piece.position}
          colors={piece.colors}
        />
      ))}
    </group>
  );
}

// Main Rubik's Cube component
function RubiksCube({ isRotating, autoRotate }) {
  return (
    <Canvas
      camera={{ position: [6, 6, 6], fov: 60 }}
      style={{ width: '100%', height: '100%' }}
      gl={{ antialias: true, alpha: true }}
    >
      {/* Enhanced Lighting */}
      <ambientLight intensity={0.3} />
      <directionalLight 
        position={[10, 10, 5]} 
        intensity={1.2}
        castShadow
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
      />
      <pointLight position={[-10, -10, -5]} intensity={0.8} />
      <spotLight
        position={[0, 10, 0]}
        angle={0.3}
        penumbra={1}
        intensity={0.5}
        castShadow
      />
      
      {/* Environment */}
      <Environment preset="city" />
      
      {/* Cube */}
      <CubeGroup isRotating={isRotating} autoRotate={autoRotate} />
      
      {/* Controls */}
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        autoRotate={false}
        autoRotateSpeed={1}
        minDistance={3}
        maxDistance={15}
        dampingFactor={0.05}
        enableDamping={true}
      />
    </Canvas>
  );
}

export default RubiksCube;
