// Enhanced logging function that will be visible in terminal
const logToTerminal = (message, data = null, type = 'INFO') => {
  const timestamp = new Date().toLocaleTimeString();
  const prefix = type === 'ERROR' ? '❌' : type === 'WARNING' ? '⚠️' : type === 'SUCCESS' ? '✅' : '🎯';
  const fullMessage = `${prefix} [${timestamp}] ${message}`;

  // Create a more structured log entry
  const logEntry = {
    timestamp: timestamp,
    type: type,
    message: message,
    data: data
  };

  // Log to browser console with enhanced formatting
  console.log(`\n${fullMessage}`);
  if (data) {
    if (typeof data === 'object') {
      console.log(JSON.stringify(data, null, 2));
    } else {
      console.log(data);
    }
  }
  console.log('='.repeat(80) + '\n');

  // Send to log server to print in terminal
  fetch('http://localhost:3001/log', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(logEntry)
  }).then(response => {
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    console.log('✅ Log sent to terminal successfully');
  }).catch(error => {
    console.error('❌ Could not send log to terminal server:', error);
    console.log('💡 Make sure the log server is running with: npm run log-server');
  });

  // Also write to local file for analysis
  const fileLogLine = `[${timestamp}] ${type}: ${message}${data ? ' | Data: ' + JSON.stringify(data) : ''}\n`;
  fetch('http://localhost:3001/write-log', {
    method: 'POST',
    headers: {
      'Content-Type': 'text/plain'
    },
    body: fileLogLine
  }).catch(error => {
    console.log('File logging not available:', error.message);
  });
};

