      <mesh position={[0, 0, 0.001]}><circleGeometry args={[size*0.45, 32]} /><meshBasicMaterial color={borderColor} /></mesh>
      <mesh position={[0, 0, 0.002]}><circleGeometry args={[size*0.4, 32]} /><meshBasicMaterial color="yellow" /></mesh>
    </group>, // Circle

    // 10-14: Triangles (flat cones to create triangle shapes) with individual face border colors
    () => <group position={[0, 0, 0.012]}>
      <mesh rotation={[Math.PI/2, 0, 0]}><coneGeometry args={[size/2, 0.01, 3]} /><meshBasicMaterial color="cyan" /></mesh>
