      <mesh position={[0, 0, 0.002]} rotation={[Math.PI/2, 0, 0]}><coneGeometry args={[size*0.4, 0.01, 3]} /><meshBasicMaterial color="pink" /></mesh>
    </group>, // Triangle
    () => <group position={[0, 0, 0.012]}>
