import React, { useRef, useMemo, useState, useCallback } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Environment } from '@react-three/drei';
import * as THREE from 'three';

// Function to create unique shapes for each piece
function createShape(pieceId, size) {
  const shapes = [
    // 0-4: Basic geometric shapes
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="red" /></mesh>, // Square
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="blue" /></mesh>, // Square
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="green" /></mesh>, // Square
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="orange" /></mesh>, // Square
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="purple" /></mesh>, // Square
    
    // 5-9: Circles
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="red" /></mesh>, // Circle
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="blue" /></mesh>, // Circle
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="green" /></mesh>, // Circle
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="orange" /></mesh>, // Circle
    () => <mesh position={[0, 0, 0.012]}><circleGeometry args={[size/2, 32]} /><meshBasicMaterial color="purple" /></mesh>, // Circle
    
    // 10-14: Triangles
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="red" /></mesh>, // Triangle (using square for now)
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="blue" /></mesh>, // Triangle
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="green" /></mesh>, // Triangle
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="orange" /></mesh>, // Triangle
    () => <mesh position={[0, 0, 0.012]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="purple" /></mesh>, // Triangle
    
    // 15-19: Diamonds
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="red" /></mesh></group>, // Diamond
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="blue" /></mesh></group>, // Diamond
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="green" /></mesh></group>, // Diamond
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="orange" /></mesh></group>, // Diamond
    () => <group position={[0, 0, 0.012]}><mesh rotation={[0, 0, Math.PI/4]}><planeGeometry args={[size, size]} /><meshBasicMaterial color="purple" /></mesh></group>, // Diamond
    
    // 20-25: Complex patterns
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="red" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="blue" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="green" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="orange" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="purple" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group>, // Square with white center
    () => <group position={[0, 0, 0.012]}><mesh><planeGeometry args={[size, size]} /><meshBasicMaterial color="cyan" /></mesh><mesh position={[0, 0, 0.001]}><planeGeometry args={[size*0.5, size*0.5]} /><meshBasicMaterial color="white" /></mesh></group> // Square with white center
  ];
  
  // Use pieceId directly to ensure unique shapes for each piece
  const shapeIndex = pieceId;
  return shapes[shapeIndex]();
}

// Individual cube piece component with proper multi-colored faces
function CubePiece({ position, colors, size = 0.95, pieceId = 0 }) {
  const meshRef = useRef();
  
  // Log piece information for debugging
  React.useEffect(() => {
    const shapeTypes = ['Square', 'Square', 'Square', 'Square', 'Square', 'Circle', 'Circle', 'Circle', 'Circle', 'Circle', 'Triangle', 'Triangle', 'Triangle', 'Triangle', 'Triangle', 'Diamond', 'Diamond', 'Diamond', 'Diamond', 'Diamond', 'Square+White', 'Square+White', 'Square+White', 'Square+White', 'Square+White', 'Square+White'];
    const shapeColors = ['Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Red', 'Blue', 'Green', 'Orange', 'Purple', 'Cyan'];
    
    // Use pieceId directly to ensure unique shapes
    const shapeType = shapeTypes[pieceId] || 'Unknown';
    const shapeColor = shapeColors[pieceId] || 'Unknown';
    
    // Focus on specific pieces for debugging
    if (pieceId === 19 || pieceId === 17 || pieceId === 1 || pieceId === 11) {
      console.log(`🔍 ${shapeColor} ${shapeType} Piece ${pieceId} at position [${position.join(', ')}]`);
      console.log(`  Stored Colors:`, colors);
      
      // Show what colors should be visible based on position
      const [x, y, z] = position;
      const visibleFaces = [];
      if (x === 1) visibleFaces.push('right');
      if (x === -1) visibleFaces.push('left');
      if (y === 1) visibleFaces.push('top');
      if (y === -1) visibleFaces.push('bottom');
      if (z === 1) visibleFaces.push('front');
      if (z === -1) visibleFaces.push('back');
      
      console.log(`  Visible faces:`, visibleFaces);
      console.log(`  Colors on visible faces:`, visibleFaces.map(face => `${face}: ${colors[face]}`));
    } else {
      console.log(`Piece ${pieceId}: ${shapeColor} ${shapeType} at position [${position.join(', ')}]`);
      console.log(`  Colors:`, colors);
    }
  }, [pieceId, position, colors]);
  
  const colorMap = {
    'white': '#ffffff',
    'yellow': '#ffd700',
    'red': '#ff0000',
    'orange': '#ff8c00',
    'blue': '#0000ff',
    'green': '#00ff00',
    'black': '#1a1a1a'
  };

  // Create a solid cube geometry for the base
  const cubeGeometry = useMemo(() => {
    return new THREE.BoxGeometry(size, size, size);
  }, [size]);

  // Create individual face geometries for each side
  const faceGeometry = useMemo(() => {
    const geometry = new THREE.PlaneGeometry(size, size);
    return geometry;
  }, [size]);

  // Get the color for a face based on the face index and position
  const getFaceColor = (faceIndex) => {
    const [x, y, z] = position;
    
    // Determine which faces are visible based on position
    const visibleFaces = [];
    if (x === 1) visibleFaces.push(2); // Right face visible
    if (x === -1) visibleFaces.push(3); // Left face visible
    if (y === 1) visibleFaces.push(4); // Top face visible
    if (y === -1) visibleFaces.push(5); // Bottom face visible
    if (z === 1) visibleFaces.push(0); // Front face visible
    if (z === -1) visibleFaces.push(1); // Back face visible
    
    // If this face is not visible, return black
    if (!visibleFaces.includes(faceIndex)) {
      return 'black';
    }
    
    // Map face index to color property
    switch (faceIndex) {
      case 0: return colors.front;  // Front face
      case 1: return colors.back;   // Back face
      case 2: return colors.right;  // Right face
      case 3: return colors.left;   // Left face
      case 4: return colors.top;    // Top face
      case 5: return colors.bottom; // Bottom face
      default: return 'black';
    }
  };

  // Face positions and rotations for a cube - faces positioned on the surface
  const faces = [
    { position: [0, 0, size/2 + 0.001], rotation: [0, 0, 0], color: getFaceColor(0), faceName: 'Front' }, // Front
    { position: [0, 0, -size/2 - 0.001], rotation: [0, Math.PI, 0], color: getFaceColor(1), faceName: 'Back' }, // Back
    { position: [size/2 + 0.001, 0, 0], rotation: [0, Math.PI/2, 0], color: getFaceColor(2), faceName: 'Right' }, // Right
    { position: [-size/2 - 0.001, 0, 0], rotation: [0, -Math.PI/2, 0], color: getFaceColor(3), faceName: 'Left' }, // Left
    { position: [0, size/2 + 0.001, 0], rotation: [-Math.PI/2, 0, 0], color: getFaceColor(4), faceName: 'Top' }, // Top
    { position: [0, -size/2 - 0.001, 0], rotation: [Math.PI/2, 0, 0], color: getFaceColor(5), faceName: 'Bottom' }  // Bottom
  ];

  return (
    <group ref={meshRef} position={position}>
      {/* Solid cube base */}
      <mesh>
        <primitive object={cubeGeometry} />
        <meshPhongMaterial 
          color="#2a2a2a"
          side={THREE.DoubleSide}
          shininess={50}
          specular={0x111111}
        />
      </mesh>
      
      {/* Colored faces on the surface */}
      {faces.map((face, index) => (
        <mesh key={index} position={face.position} rotation={face.rotation}>
          <primitive object={faceGeometry} />
          <meshPhongMaterial 
            color={colorMap[face.color] || colorMap['black']}
            side={THREE.FrontSide}
            shininess={100}
            specular={0x222222}
            emissive={colorMap[face.color] || colorMap['black']}
            emissiveIntensity={0.3}
          />
        </mesh>
      ))}
      
      {/* Shape identifiers on ALL faces (including black ones) */}
      {faces.map((face, index) => {
        return (
          <group key={`shape-${index}`} position={face.position} rotation={face.rotation}>
            {/* Large white background */}
            <mesh position={[0, 0, 0.01]}>
              <planeGeometry args={[size * 0.5, size * 0.5]} />
              <meshBasicMaterial color="white" />
            </mesh>
            {/* Black border */}
            <mesh position={[0, 0, 0.011]}>
              <planeGeometry args={[size * 0.45, size * 0.45]} />
              <meshBasicMaterial color="black" />
            </mesh>
            {/* Shape identifier */}
            {createShape(pieceId, size * 0.3)}
          </group>
        );
      })}
      
      {/* Center piece identifier with shape */}
      <mesh position={[0, 0, 0.005]}>
        <planeGeometry args={[size * 0.7, size * 0.7]} />
        <meshBasicMaterial color="yellow" />
      </mesh>
      <mesh position={[0, 0, 0.006]}>
        <planeGeometry args={[size * 0.6, size * 0.6]} />
        <meshBasicMaterial color="black" />
      </mesh>
      {createShape(pieceId, size * 0.4)}
    </group>
  );
}

// Cube state management and rotation logic
function useCubeState() {
  const [cubeState, setCubeState] = useState(() => {
    // Initialize solved cube state - track positions and original colors
    // A real Rubik's cube has 26 pieces (not 27) - the center core is missing
    const state = [];
    for (let x = -1; x <= 1; x++) {
      for (let y = -1; y <= 1; y++) {
        for (let z = -1; z <= 1; z++) {
          // Skip the center piece (0, 0, 0) - this is the core mechanism
          if (x === 0 && y === 0 && z === 0) continue;
          
          const position = [x, y, z];
          // Store original colors for each face based on solved state
          // Each piece gets colors based on its position type (corner, edge, center)
          const colors = {
            front: 'black',
            back: 'black', 
            right: 'black',
            left: 'black',
            top: 'black',
            bottom: 'black'
          };
          
          // Determine piece type and assign appropriate colors
          const isCorner = (x !== 0 && y !== 0 && z !== 0);
          const isEdge = ((x === 0 && y !== 0 && z !== 0) || 
                         (x !== 0 && y === 0 && z !== 0) || 
                         (x !== 0 && y !== 0 && z === 0));
          const isCenter = ((x === 0 && y === 0 && z !== 0) || 
                           (x === 0 && y !== 0 && z === 0) || 
                           (x !== 0 && y === 0 && z === 0));
          
          if (isCorner) {
            // Corner pieces: 3 colors based on which faces they touch in solved state
            if (z === 1) colors.front = 'white';
            if (z === -1) colors.back = 'yellow';
            if (x === 1) colors.right = 'red';
            if (x === -1) colors.left = 'orange';
            if (y === 1) colors.top = 'blue';
            if (y === -1) colors.bottom = 'green';
          } else if (isEdge) {
            // Edge pieces: 2 colors based on which faces they touch in solved state
            if (z === 1) colors.front = 'white';
            if (z === -1) colors.back = 'yellow';
            if (x === 1) colors.right = 'red';
            if (x === -1) colors.left = 'orange';
            if (y === 1) colors.top = 'blue';
            if (y === -1) colors.bottom = 'green';
          } else if (isCenter) {
            // Center pieces: 1 specific color each
            if (z === 1) colors.front = 'white';
            if (z === -1) colors.back = 'yellow';
            if (x === 1) colors.right = 'red';
            if (x === -1) colors.left = 'orange';
            if (y === 1) colors.top = 'blue';
            if (y === -1) colors.bottom = 'green';
          }
          state.push({ position: [...position], colors });
        }
      }
    }
    return state;
  });

  const [isAnimating, setIsAnimating] = useState(false);
  const [animationQueue, setAnimationQueue] = useState([]);

  // Rotation functions for each face
  const rotateFace = useCallback((face, direction) => {
    if (isAnimating) return;
    
    setIsAnimating(true);
    
    setCubeState(prevState => {
      const newState = [...prevState];
      
      // Define which pieces belong to each face
      const facePieces = {
        'F': newState.filter(p => p.position[2] === 1), // Front face
        'B': newState.filter(p => p.position[2] === -1), // Back face
        'R': newState.filter(p => p.position[0] === 1), // Right face
        'L': newState.filter(p => p.position[0] === -1), // Left face
        'U': newState.filter(p => p.position[1] === 1), // Up face
        'D': newState.filter(p => p.position[1] === -1) // Down face
      };

      const pieces = facePieces[face];
      if (!pieces) return prevState;

        // Apply rotation based on face and direction
        pieces.forEach(piece => {
          const [x, y, z] = piece.position;
          let newX = x, newY = y, newZ = z;
          let newColors = { ...piece.colors };
          
          // Debug purple diamond piece 19 and green diamond piece 17
          if (piece.pieceId === 19 || piece.pieceId === 17) {
            console.log(`🔄 BEFORE ${face} rotation - Piece ${piece.pieceId}:`, newColors);
          }
          
          // Apply 3D transformation and color rotation
          switch (face) {
            case 'F': // Front face rotation
              if (direction === 'clockwise') {
                newX = -y;
                newY = x;
                // 3D color rotation: when piece rotates around Z-axis
                // Colors rotate in the XY plane: top->right, right->bottom, bottom->left, left->top
                const temp = newColors.top;
                newColors.top = newColors.left;
                newColors.left = newColors.bottom;
                newColors.bottom = newColors.right;
                newColors.right = temp;
              } else {
                newX = y;
                newY = -x;
                // 3D color rotation: when piece rotates around Z-axis (reverse)
                // Colors rotate in the XY plane: top->left, left->bottom, bottom->right, right->top
                const temp = newColors.top;
                newColors.top = newColors.right;
                newColors.right = newColors.bottom;
                newColors.bottom = newColors.left;
                newColors.left = temp;
              }
              break;
            case 'B': // Back face rotation
              if (direction === 'clockwise') {
                newX = y;
                newY = -x;
                // Rotate colors: top->left, left->bottom, bottom->right, right->top
                const temp = newColors.top;
                newColors.top = newColors.right;
                newColors.right = newColors.bottom;
                newColors.bottom = newColors.left;
                newColors.left = temp;
              } else {
                newX = -y;
                newY = x;
                // Rotate colors: top->right, right->bottom, bottom->left, left->top
                const temp = newColors.top;
                newColors.top = newColors.left;
                newColors.left = newColors.bottom;
                newColors.bottom = newColors.right;
                newColors.right = temp;
              }
              break;
            case 'R': // Right face rotation
              if (direction === 'clockwise') {
                newZ = -y;
                newY = z;
                // Rotate colors: top->front, front->bottom, bottom->back, back->top
                const temp = newColors.top;
                newColors.top = newColors.back;
                newColors.back = newColors.bottom;
                newColors.bottom = newColors.front;
                newColors.front = temp;
              } else {
                newZ = y;
                newY = -z;
                // Rotate colors: top->back, back->bottom, bottom->front, front->top
                const temp = newColors.top;
                newColors.top = newColors.front;
                newColors.front = newColors.bottom;
                newColors.bottom = newColors.back;
                newColors.back = temp;
              }
              break;
            case 'L': // Left face rotation
              if (direction === 'clockwise') {
                newZ = y;
                newY = -z;
                // Rotate colors: top->back, back->bottom, bottom->front, front->top
                const temp = newColors.top;
                newColors.top = newColors.front;
                newColors.front = newColors.bottom;
                newColors.bottom = newColors.back;
                newColors.back = temp;
              } else {
                newZ = -y;
                newY = z;
                // Rotate colors: top->front, front->bottom, bottom->back, back->top
                const temp = newColors.top;
                newColors.top = newColors.back;
                newColors.back = newColors.bottom;
                newColors.bottom = newColors.front;
                newColors.front = temp;
              }
              break;
            case 'U': // Up face rotation
              if (direction === 'clockwise') {
                newX = z;
                newZ = -x;
                // Rotate colors: front->right, right->back, back->left, left->front
                const temp = newColors.front;
                newColors.front = newColors.left;
                newColors.left = newColors.back;
                newColors.back = newColors.right;
                newColors.right = temp;
              } else {
                newX = -z;
                newZ = x;
                // Rotate colors: front->left, left->back, back->right, right->front
                const temp = newColors.front;
                newColors.front = newColors.right;
                newColors.right = newColors.back;
                newColors.back = newColors.left;
                newColors.left = temp;
              }
              break;
            case 'D': // Down face rotation
              if (direction === 'clockwise') {
                newX = -z;
                newZ = x;
                // Rotate colors: front->left, left->back, back->right, right->front
                const temp = newColors.front;
                newColors.front = newColors.right;
                newColors.right = newColors.back;
                newColors.back = newColors.left;
                newColors.left = temp;
              } else {
                newX = z;
                newZ = -x;
                // Rotate colors: front->right, right->back, back->left, left->front
                const temp = newColors.front;
                newColors.front = newColors.left;
                newColors.left = newColors.back;
                newColors.back = newColors.right;
                newColors.right = temp;
              }
              break;
          }
          
          // Debug purple diamond piece 19 and green diamond piece 17
          if (piece.pieceId === 19 || piece.pieceId === 17) {
            console.log(`🔄 AFTER ${face} rotation - Piece ${piece.pieceId}:`, newColors);
          }
          
          piece.position = [newX, newY, newZ];
          piece.colors = newColors;
        });

      return newState;
    });
    
    // Animation delay
    setTimeout(() => setIsAnimating(false), 300);
  }, [isAnimating]);

  // Execute scramble moves
  const executeScramble = useCallback((queue) => {
    if (queue.length === 0) {
      setIsAnimating(false);
      return;
    }
    
    const { move, direction } = queue[0];
    console.log(`Executing move: ${move} ${direction}`);
    
    // Apply the rotation immediately
    setCubeState(prevState => {
      const newState = [...prevState];
      
      // Define which pieces belong to each face
      const facePieces = {
        'F': newState.filter(p => p.position[2] === 1), // Front face
        'B': newState.filter(p => p.position[2] === -1), // Back face
        'R': newState.filter(p => p.position[0] === 1), // Right face
        'L': newState.filter(p => p.position[0] === -1), // Left face
        'U': newState.filter(p => p.position[1] === 1), // Up face
        'D': newState.filter(p => p.position[1] === -1) // Down face
      };

      const pieces = facePieces[move];
      if (!pieces) return prevState;

        // Apply rotation based on face and direction
        pieces.forEach(piece => {
          const [x, y, z] = piece.position;
          let newX = x, newY = y, newZ = z;
          let newColors = { ...piece.colors };
          
          // Debug purple diamond piece 19 and green diamond piece 17 in executeScramble
          if (piece.pieceId === 19 || piece.pieceId === 17) {
            console.log(`🎲 BEFORE ${move} move - Piece ${piece.pieceId}:`, newColors);
          }
          
          switch (move) {
            case 'F': // Front face rotation
              if (direction === 'clockwise') {
                newX = -y;
                newY = x;
                // Rotate colors: top->right, right->bottom, bottom->left, left->top
                const temp = newColors.top;
                newColors.top = newColors.left;
                newColors.left = newColors.bottom;
                newColors.bottom = newColors.right;
                newColors.right = temp;
              } else {
                newX = y;
                newY = -x;
                // Rotate colors: top->left, left->bottom, bottom->right, right->top
                const temp = newColors.top;
                newColors.top = newColors.right;
                newColors.right = newColors.bottom;
                newColors.bottom = newColors.left;
                newColors.left = temp;
              }
              break;
            case 'B': // Back face rotation
              if (direction === 'clockwise') {
                newX = y;
                newY = -x;
                // Rotate colors: top->left, left->bottom, bottom->right, right->top
                const temp = newColors.top;
                newColors.top = newColors.right;
                newColors.right = newColors.bottom;
                newColors.bottom = newColors.left;
                newColors.left = temp;
              } else {
                newX = -y;
                newY = x;
                // Rotate colors: top->right, right->bottom, bottom->left, left->top
                const temp = newColors.top;
                newColors.top = newColors.left;
                newColors.left = newColors.bottom;
                newColors.bottom = newColors.right;
                newColors.right = temp;
              }
              break;
            case 'R': // Right face rotation
              if (direction === 'clockwise') {
                newZ = -y;
                newY = z;
                // Rotate colors: top->front, front->bottom, bottom->back, back->top
                const temp = newColors.top;
                newColors.top = newColors.back;
                newColors.back = newColors.bottom;
                newColors.bottom = newColors.front;
                newColors.front = temp;
              } else {
                newZ = y;
                newY = -z;
                // Rotate colors: top->back, back->bottom, bottom->front, front->top
                const temp = newColors.top;
                newColors.top = newColors.front;
                newColors.front = newColors.bottom;
                newColors.bottom = newColors.back;
                newColors.back = temp;
              }
              break;
            case 'L': // Left face rotation
              if (direction === 'clockwise') {
                newZ = y;
                newY = -z;
                // Rotate colors: top->back, back->bottom, bottom->front, front->top
                const temp = newColors.top;
                newColors.top = newColors.front;
                newColors.front = newColors.bottom;
                newColors.bottom = newColors.back;
                newColors.back = temp;
              } else {
                newZ = -y;
                newY = z;
                // Rotate colors: top->front, front->bottom, bottom->back, back->top
                const temp = newColors.top;
                newColors.top = newColors.back;
                newColors.back = newColors.bottom;
                newColors.bottom = newColors.front;
                newColors.front = temp;
              }
              break;
            case 'U': // Up face rotation
              if (direction === 'clockwise') {
                newX = z;
                newZ = -x;
                // Rotate colors: front->right, right->back, back->left, left->front
                const temp = newColors.front;
                newColors.front = newColors.left;
                newColors.left = newColors.back;
                newColors.back = newColors.right;
                newColors.right = temp;
              } else {
                newX = -z;
                newZ = x;
                // Rotate colors: front->left, left->back, back->right, right->front
                const temp = newColors.front;
                newColors.front = newColors.right;
                newColors.right = newColors.back;
                newColors.back = newColors.left;
                newColors.left = temp;
              }
              break;
            case 'D': // Down face rotation
              if (direction === 'clockwise') {
                newX = -z;
                newZ = x;
                // Rotate colors: front->left, left->back, back->right, right->front
                const temp = newColors.front;
                newColors.front = newColors.right;
                newColors.right = newColors.back;
                newColors.back = newColors.left;
                newColors.left = temp;
              } else {
                newX = z;
                newZ = -x;
                // Rotate colors: front->right, right->back, back->left, left->front
                const temp = newColors.front;
                newColors.front = newColors.left;
                newColors.left = newColors.back;
                newColors.back = newColors.right;
                newColors.right = temp;
              }
              break;
          }
          
          // Debug purple diamond piece 19 and green diamond piece 17 in executeScramble
          if (piece.pieceId === 19 || piece.pieceId === 17) {
            console.log(`🎲 AFTER ${move} move - Piece ${piece.pieceId}:`, newColors);
          }
          
          piece.position = [newX, newY, newZ];
          piece.colors = newColors;
        });

      return newState;
    });
    
    // Continue with the next move after a delay
    setTimeout(() => {
      executeScramble(queue.slice(1));
    }, 350);
  }, []);

  // Scramble function using standard notation
  const scramble = useCallback(() => {
    console.log('Scramble function called');
    if (isAnimating) {
      console.log('Already animating, skipping scramble');
      return;
    }
    
    setIsAnimating(true);
    console.log('Starting scramble animation');
    
    const moves = ['F', 'B', 'R', 'L', 'U', 'D'];
    const directions = ['clockwise', 'counterclockwise'];
    const scrambleLength = 20;
    
    let currentQueue = [];
    let lastMove = '';
    
    for (let i = 0; i < scrambleLength; i++) {
      let move;
      let direction;
      
      // Avoid doing the same move twice in a row
      do {
        move = moves[Math.floor(Math.random() * moves.length)];
      } while (move === lastMove);
      
      direction = directions[Math.floor(Math.random() * directions.length)];
      lastMove = move;
      
      currentQueue.push({ move, direction });
    }
    
    console.log('Generated scramble queue:', currentQueue);
    setAnimationQueue(currentQueue);
    executeScramble(currentQueue);
  }, [isAnimating, executeScramble]);

  // Reset function
  const reset = useCallback(() => {
    if (isAnimating) return;
    
    setCubeState(() => {
      const state = [];
      for (let x = -1; x <= 1; x++) {
        for (let y = -1; y <= 1; y++) {
          for (let z = -1; z <= 1; z++) {
            // Skip the center piece (0, 0, 0) - this is the core mechanism
            if (x === 0 && y === 0 && z === 0) continue;
            
            const position = [x, y, z];
            // Store original colors for each face based on solved state
            // Each piece gets colors based on its position type (corner, edge, center)
            const colors = {
              front: 'black',
              back: 'black', 
              right: 'black',
              left: 'black',
              top: 'black',
              bottom: 'black'
            };
            
            // Determine piece type and assign appropriate colors
            const isCorner = (x !== 0 && y !== 0 && z !== 0);
            const isEdge = ((x === 0 && y !== 0 && z !== 0) || 
                           (x !== 0 && y === 0 && z !== 0) || 
                           (x !== 0 && y !== 0 && z === 0));
            const isCenter = ((x === 0 && y === 0 && z !== 0) || 
                             (x === 0 && y !== 0 && z === 0) || 
                             (x !== 0 && y === 0 && z === 0));
            
            if (isCorner) {
              // Corner pieces: 3 colors based on which faces they touch in solved state
              if (z === 1) colors.front = 'white';
              if (z === -1) colors.back = 'yellow';
              if (x === 1) colors.right = 'red';
              if (x === -1) colors.left = 'orange';
              if (y === 1) colors.top = 'blue';
              if (y === -1) colors.bottom = 'green';
            } else if (isEdge) {
              // Edge pieces: 2 colors based on which faces they touch in solved state
              if (z === 1) colors.front = 'white';
              if (z === -1) colors.back = 'yellow';
              if (x === 1) colors.right = 'red';
              if (x === -1) colors.left = 'orange';
              if (y === 1) colors.top = 'blue';
              if (y === -1) colors.bottom = 'green';
            } else if (isCenter) {
              // Center pieces: 1 specific color each
              if (z === 1) colors.front = 'white';
              if (z === -1) colors.back = 'yellow';
              if (x === 1) colors.right = 'red';
              if (x === -1) colors.left = 'orange';
              if (y === 1) colors.top = 'blue';
              if (y === -1) colors.bottom = 'green';
            }
            state.push({ position: [...position], colors });
          }
        }
      }
      return state;
    });
  }, [isAnimating]);

  return {
    cubeState,
    isAnimating,
    rotateFace,
    scramble,
    reset
  };
}

// Main cube group component with enhanced structure
function CubeGroup({ isRotating, autoRotate = false, onScramble, onReset }) {
  const groupRef = useRef();
  const [rotationSpeed] = useState({ x: 0.005, y: 0.01 });
  
  const { cubeState, isAnimating, rotateFace, scramble, reset } = useCubeState();
  
  // Expose functions to parent
  React.useEffect(() => {
    if (onScramble) onScramble(scramble);
    if (onReset) onReset(reset);
  }, [onScramble, onReset, scramble, reset]);
  
  useFrame((state) => {
    if (autoRotate && groupRef.current && !isAnimating) {
      groupRef.current.rotation.y += rotationSpeed.y;
      groupRef.current.rotation.x += rotationSpeed.x;
    }
  });

        return (
          <group ref={groupRef}>
            {cubeState.map((piece, index) => (
              <CubePiece
                key={index}
                position={piece.position}
                colors={piece.colors}
                pieceId={index}
              />
            ))}
          </group>
        );
}

// Main Rubik's Cube component
function RubiksCube({ isRotating, autoRotate = false, onScramble, onReset }) {
  return (
    <Canvas
      camera={{ position: [6, 6, 6], fov: 60 }}
      style={{ width: '100%', height: '100%' }}
      gl={{ antialias: true, alpha: true }}
    >
      {/* Enhanced Lighting */}
      <ambientLight intensity={1.2} />
      <directionalLight 
        position={[10, 10, 5]} 
        intensity={3.0}
        castShadow
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
      />
      <pointLight position={[-10, -10, -5]} intensity={2.0} />
      <pointLight position={[10, -10, 5]} intensity={2.0} />
      <pointLight position={[0, 0, 10]} intensity={1.5} />
      <spotLight
        position={[0, 10, 0]}
        angle={0.5}
        penumbra={0.5}
        intensity={2.0}
        castShadow
      />
      
      {/* Environment */}
      <Environment preset="city" />
      
      {/* Cube */}
      <CubeGroup 
        isRotating={isRotating} 
        autoRotate={autoRotate}
        onScramble={onScramble}
        onReset={onReset}
      />
      
      {/* Controls */}
      <OrbitControls 
        enablePan={true}
        enableZoom={true}
        enableRotate={true}
        autoRotate={false}
        autoRotateSpeed={1}
        minDistance={3}
        maxDistance={15}
        dampingFactor={0.05}
        enableDamping={true}
      />
    </Canvas>
  );
}

export default RubiksCube;
