import React, { useState } from 'react';
import styled from 'styled-components';

const IdentifyContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 20px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.2);
  max-height: 500px;
  overflow-y: auto;
`;

const SectionTitle = styled.h3`
  color: white;
  text-align: center;
  margin: 0 0 15px 0;
  font-size: 1.3rem;
  font-weight: 600;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
`;

const ButtonGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 15px;
  margin-bottom: 20px;
`;

const IdentifyButton = styled.button`
  padding: 12px 16px;
  border: 2px solid ${props => {
    if (props.$isActive) return '#4CAF50';
    if (props.$hasIncorrect) return '#ff4757';
    return 'rgba(255, 255, 255, 0.3)';
  }};
  border-radius: 10px;
  font-size: 14px;
  font-weight: 600;
  cursor: ${props => props.disabled ? 'not-allowed' : 'pointer'};
  transition: all 0.3s ease;
  background: ${props => {
    if (props.disabled) return 'rgba(100, 100, 100, 0.3)';
    if (props.$isActive) return 'rgba(76, 175, 80, 0.2)';
    if (props.$hasIncorrect) return 'rgba(255, 71, 87, 0.2)';
    return 'rgba(255, 255, 255, 0.1)';
  }};
  color: ${props => {
    if (props.disabled) return '#666';
    if (props.$isActive) return '#4CAF50';
    if (props.$hasIncorrect) return '#ff4757';
    return 'white';
  }};
  opacity: ${props => props.disabled ? 0.5 : 1};
  
  &:hover {
    transform: ${props => props.disabled ? 'none' : 'translateY(-2px)'};
    box-shadow: ${props => props.disabled ? 'none' : '0 4px 12px rgba(0, 0, 0, 0.3)'};
    background: ${props => {
      if (props.disabled) return 'rgba(100, 100, 100, 0.3)';
      if (props.$isActive) return 'rgba(76, 175, 80, 0.3)';
      if (props.$hasIncorrect) return 'rgba(255, 71, 87, 0.3)';
      return 'rgba(255, 255, 255, 0.2)';
    }};
  }
  
  &:active {
    transform: ${props => props.disabled ? 'none' : 'translateY(0)'};
  }
`;

const ButtonContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4px;
`;

const ButtonTitle = styled.span`
  font-size: 14px;
  font-weight: 700;
`;

const ButtonSubtitle = styled.span`
  font-size: 11px;
  opacity: 0.8;
`;

const CountBadge = styled.span`
  background: ${props => props.$hasIncorrect ? '#ff4757' : '#4CAF50'};
  color: white;
  border-radius: 50%;
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 10px;
  font-weight: bold;
  margin-top: 4px;
`;

const Instructions = styled.p`
  color: rgba(255, 255, 255, 0.8);
  font-size: 13px;
  text-align: center;
  margin: 0 0 15px 0;
  line-height: 1.4;
`;

const StatusMessage = styled.div`
  padding: 10px;
  border-radius: 8px;
  text-align: center;
  font-size: 12px;
  font-weight: 600;
  background: ${props => {
    if (props.$type === 'success') return 'rgba(76, 175, 80, 0.2)';
    if (props.$type === 'warning') return 'rgba(255, 193, 7, 0.2)';
    if (props.$type === 'error') return 'rgba(244, 67, 54, 0.2)';
    return 'rgba(33, 150, 243, 0.2)';
  }};
  color: ${props => {
    if (props.$type === 'success') return '#4CAF50';
    if (props.$type === 'warning') return '#FFC107';
    if (props.$type === 'error') return '#f44336';
    return '#2196F3';
  }};
  border: 1px solid ${props => {
    if (props.$type === 'success') return 'rgba(76, 175, 80, 0.3)';
    if (props.$type === 'warning') return 'rgba(255, 193, 7, 0.3)';
    if (props.$type === 'error') return 'rgba(244, 67, 54, 0.3)';
    return 'rgba(33, 150, 243, 0.3)';
  }};
`;

// Custom logging function
const logToTerminal = (message, data = null) => {
  console.log(`\nðŸŽ¯ ${message}`);
  if (data) {
    console.log(JSON.stringify(data, null, 2));
  }
  console.log('='.repeat(80) + '\n');
  
  // Send to log server
  fetch('http://localhost:3001/log', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({
      message: message,
      data: data
    })
  })
  .then(response => {
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    console.log('âœ… Log sent to terminal successfully');
  })
  .catch(error => {
    console.error('âŒ Could not send log to terminal server:', error);
  });
};

function IdentifyIncorrectShapes({ isScrambling, cubeState, onIdentification }) {
  const [activeIdentification, setActiveIdentification] = useState(null);
  const [identificationResults, setIdentificationResults] = useState({});

  // Helper functions
  const getShapeType = (pieceId) => {
    const shapeTypes = [
      'Square', 'Square', 'Square', 'Square', 'Square',
      'Circle', 'Circle', 'Circle', 'Circle', 'Circle',
      'Triangle', 'Triangle', 'Triangle', 'Triangle', 'Diamond',
      'Diamond', 'Diamond', 'Diamond', 'Diamond', 'Diamond',
      'Triangle', 'Triangle', 'Triangle', 'Triangle', 'Triangle', 'Triangle'
    ];
    return shapeTypes[pieceId] || 'Unknown';
  };

  const getShapeColor = (pieceId) => {
    const shapeColors = [
      'Red', 'Blue', 'Green', 'Orange', 'Purple',
      'Red', 'Blue', 'Green', 'Orange', 'Yellow',
      'Cyan', 'Magenta', 'Lime', 'Pink', 'Purple',
      'Red', 'Blue', 'Green', 'Orange', 'Purple',
      'Cyan', 'Magenta', 'Lime', 'Pink', 'Purple', 'Yellow'
    ];
    return shapeColors[pieceId] || 'Unknown';
  };

  const getExpectedPosition = (pieceId) => {
    const positions = [];
    for (let x = -1; x <= 1; x++) {
      for (let y = -1; y <= 1; y++) {
        for (let z = -1; z <= 1; z++) {
          if (x === 0 && y === 0 && z === 0) continue;
          positions.push([x, y, z]);
        }
      }
    }
    return positions[pieceId];
  };

  const isPieceInCorrectPosition = (piece, pieceId) => {
    if (!piece || !piece.position) return false;
    const expectedPosition = getExpectedPosition(pieceId);
    return JSON.stringify(piece.position) === JSON.stringify(expectedPosition);
  };

  const getExpectedBorderColor = (faceIndex) => {
    const borderColors = [
      '#FFFFFF', // Face 0 (front) - White
      '#FF8C00', // Face 1 (back) - Orange  
      '#00FF00', // Face 2 (right) - Green
      '#DC143C', // Face 3 (left) - Red
      '#0000FF', // Face 4 (top) - Blue
      '#FFD700'  // Face 5 (bottom) - Yellow
    ];
    return borderColors[faceIndex] || '#000000';
  };

  const getBorderColorName = (faceIndex) => {
    const colorNames = ['White', 'Orange', 'Green', 'Red', 'Blue', 'Yellow'];
    return colorNames[faceIndex] || 'Unknown';
  };

  // Get pieces data
  const getPieces = () => {
    if (Array.isArray(cubeState)) {
      return cubeState.map((piece, index) => ({
        pieceId: index,
        position: piece.position,
        colors: piece.colors,
        rotationHistory: piece.rotationHistory || []
      }));
    } else if (cubeState?.pieces) {
      return cubeState.pieces;
    }
    return [];
  };

  const pieces = getPieces();

  // Identify incorrect specific piece (shape+color+border combination)
  const identifyIncorrectPiece = (pieceId) => {
    const piece = pieces.find(p => p.pieceId === pieceId);
    if (!piece) return null;

    const isInCorrectPosition = isPieceInCorrectPosition(piece, pieceId);
    const shapeType = getShapeType(pieceId);
    const shapeColor = getShapeColor(pieceId);
    const expectedPosition = getExpectedPosition(pieceId);

    // Get the expected border colors for this piece's current position
    const [x, y, z] = piece.position;
    const expectedBorderColors = [];
    
    if (x === 1) expectedBorderColors.push({ faceIndex: 2, color: 'Green', hex: '#00FF00' }); // Right face
    if (x === -1) expectedBorderColors.push({ faceIndex: 3, color: 'Red', hex: '#DC143C' }); // Left face
    if (y === 1) expectedBorderColors.push({ faceIndex: 4, color: 'Blue', hex: '#0000FF' }); // Top face
    if (y === -1) expectedBorderColors.push({ faceIndex: 5, color: 'Yellow', hex: '#FFD700' }); // Bottom face
    if (z === 1) expectedBorderColors.push({ faceIndex: 0, color: 'White', hex: '#FFFFFF' }); // Front face
    if (z === -1) expectedBorderColors.push({ faceIndex: 1, color: 'Orange', hex: '#FF8C00' }); // Back face

    const result = {
      type: 'piece',
      pieceId,
      shapeType,
      shapeColor,
      expectedBorderColors,
      isInCorrectPosition,
      currentPosition: piece.position,
      expectedPosition,
      colors: piece.colors,
      rotationHistory: piece.rotationHistory,
      count: isInCorrectPosition ? 0 : 1
    };

    setIdentificationResults(prev => ({
      ...prev,
      [`piece-${pieceId}`]: result
    }));

    logToTerminal(`ðŸŽ¯ IDENTIFIED PIECE ${pieceId}: ${shapeColor} ${shapeType} with ${expectedBorderColors.map(b => b.color).join(', ')} borders`, result);
    
    if (onIdentification) {
      onIdentification(result);
    }

    return result;
  };

  const handleButtonClick = (pieceId) => {
    if (isScrambling) return;

    setActiveIdentification(`piece-${pieceId}`);
    const result = identifyIncorrectPiece(pieceId);

    // Reset active state after a short delay
    setTimeout(() => {
      setActiveIdentification(null);
    }, 2000);
  };

  const getButtonState = (pieceId) => {
    const key = `piece-${pieceId}`;
    const result = identificationResults[key];
    const isActive = activeIdentification === key;
    
    return {
      isActive,
      hasIncorrect: result && !result.isInCorrectPosition,
      isCorrect: result && result.isInCorrectPosition
    };
  };

  if (!cubeState || pieces.length === 0) {
    return (
      <IdentifyContainer>
        <SectionTitle>Identify Incorrect Shapes</SectionTitle>
        <StatusMessage $type="warning">
          Loading cube state... Please scramble the cube first to identify incorrect shapes.
        </StatusMessage>
      </IdentifyContainer>
    );
  }

  return (
    <IdentifyContainer>
      <SectionTitle>Identify Incorrect Shapes</SectionTitle>
      <Instructions>
        Click any button below to identify pieces with incorrect border colors for that specific face.
        Buttons show the count of incorrect pieces found for each border color.
      </Instructions>

      {/* Border Color Identification */}
      <div>
        <h4 style={{ color: 'white', marginBottom: '15px', fontSize: '16px' }}>By Border Color</h4>
        <ButtonGrid style={{ gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))' }}>
          {[0, 1, 2, 3, 4, 5].map(faceIndex => {
            const state = getButtonState(faceIndex);
            const borderColorName = getBorderColorName(faceIndex);
            const borderColorHex = getExpectedBorderColor(faceIndex);
            
            return (
              <IdentifyButton
                key={`border-${faceIndex}`}
                onClick={() => handleButtonClick(faceIndex)}
                disabled={isScrambling}
                $isActive={state.isActive}
                $hasIncorrect={state.hasIncorrect}
                style={{
                  background: state.isActive 
                    ? 'rgba(76, 175, 80, 0.3)' 
                    : state.hasIncorrect 
                      ? 'rgba(255, 71, 87, 0.2)' 
                      : 'rgba(255, 255, 255, 0.1)',
                  borderColor: state.hasIncorrect ? '#ff4757' : borderColorHex,
                  borderWidth: '3px'
                }}
              >
                <ButtonContent>
                  <ButtonTitle style={{ color: borderColorHex, fontWeight: 'bold' }}>
                    {borderColorName} Borders
                  </ButtonTitle>
                  <ButtonSubtitle>
                    Identify incorrect {borderColorName.toLowerCase()} border colors
                  </ButtonSubtitle>
                  {state.count > 0 && (
                    <CountBadge $hasIncorrect={state.hasIncorrect}>
                      {state.count}
                    </CountBadge>
                  )}
                </ButtonContent>
              </IdentifyButton>
            );
          })}
        </ButtonGrid>
      </div>

      {/* Status Message */}
      {activeIdentification && (
        <StatusMessage $type="success">
          Border color identification complete! Check the terminal for detailed results.
        </StatusMessage>
      )}
    </IdentifyContainer>
  );
}

export default IdentifyIncorrectShapes;
